const html = $input.first().json.data;

// Function to clean the text extracted from the HTTP request node
function cleanText(text) {
  if (!text) return '';
  return text
    .replace(/<[^>]*>/g, '') 
    .replace(/&nbsp;/g, ' ') 
    .replace(/&amp;/g, '&')  
    .replace(/&lt;/g, '<')   
    .replace(/&gt;/g, '>')   
    .replace(/&quot;/g, '"') 
    .replace(/\\n/g, '\n')   
    .replace(/\s+/g, ' ')    
    .trim();
}

// Episode Meta Data for record
const episodeUrl = $('Podcasts from RSS Feed').first().json.link;
const episodeMatch = episodeUrl.match(/ep-(\d+)/);
const episodeId = episodeMatch ? episodeMatch[1] : 'unknown';
const episodeCode = episodeMatch ? episodeMatch[0] : 'unknown';
const episodeTitle = $('Podcasts from RSS Feed').first().json.title|| 'Unknown Title';

// Extract the vocabulary from the specific section
const vocabMatch = html.match(/<h3>Vocabulary<\/h3>([\s\S]*?)(?=<h3>|<p><strong style)/);
let vocabularyItems = [];

if (vocabMatch && vocabMatch[1]) {
  const vocabHtml = vocabMatch[1];

  // Parse vocabulary entries (format: <strong>word</strong><br />definition<br />&nbsp;)
  const vocabRegex = /<strong>(.*?)<\/strong><br \/>(.*?)(?=<br \/>|<\/p>)/gs;
  const matches = [...vocabHtml.matchAll(vocabRegex)];

  matches.forEach(match => {
    const word = cleanText(match[1]);
    const definition = cleanText(match[2]);

    if (word && definition && word !== '&nbsp;') {
      vocabularyItems.push({
        word: word,
        definition: definition
      });
    }
  });
}

// Extract Transcript
const transcriptMatch = html.match(/<strong style="font-size: 1\.17em;">TRANSCRIPT<\/strong><\/p>([\s\S]*?)(?=<h3>Next<\/h3>|<div class="widget widget-list)/);
let transcriptText = '';

if (transcriptMatch && transcriptMatch[1]) {
  const paragraphs = transcriptMatch[1].match(/<p[^>]*>(.*?)<\/p>/gs);

  if (paragraphs) {
    transcriptText = paragraphs
      .map(p => cleanText(p))
      .filter(t => t.length > 0 && !t.startsWith('Note:')) // Remove note disclaimer
      .join('\n\n');
  }
}

// Extract Episode Description
const descriptionMatch = html.match(/<meta name="description" content="([^"]+)"/);
const description = descriptionMatch ? descriptionMatch[1] : '';

// Extract Links for download
const downloadLinks = {
  pdf: '',
  audio: '',
  transcript: '',
  podcast: ''
};
// PDF link
const pdfMatch = html.match(/href="(https:\/\/downloads\.bbc\.co\.uk\/learningenglish\/features\/6min\/[^"]*\.pdf)"/);
if (pdfMatch) {
  downloadLinks.pdf = pdfMatch[1];
  downloadLinks.transcript = pdfMatch[1]; // Same as PDF in this case
}
// Audio MP3 link
const audioMatch = html.match(/href="(https:\/\/downloads\.bbc\.co\.uk\/learningenglish\/features\/6min\/[^"]*\.mp3)"/);
if (audioMatch) {
  downloadLinks.audio = audioMatch[1];
}
// Podcast link
const podcastMatch = html.match(/href="(https:\/\/www\.bbc\.co\.uk\/programmes\/p02pc9tn\/episodes\/downloads)"/);
if (podcastMatch) {
  downloadLinks.podcast = podcastMatch[1];
}

return {
  json: {
    // Episode metadata
    episode_id: episodeId,
    episode_code: episodeCode,
    episode_url: episodeUrl,
    episode_title: cleanText(episodeTitle),
    description: description,
    processed_date: new Date().toISOString().split('T')[0],

    // Main content
    transcript: transcriptText,
    transcript_word_count: transcriptText.split(/\s+/).length,

    // Vocabulary (BBC's own selection)
    vocabulary_items: vocabularyItems,
    vocabulary_count: vocabularyItems.length,

    // Download links
    download_links: downloadLinks
  }
};

// Data from the parsing node
