const practiceExercises = aiPracticeContent
  .split('\n')
  .filter(line => line.trim().length > 0)
  .filter(line => line.includes('_____'));

// Discussion Questions from AI
const aiDiscussionContent = $('Discussion Questions').first().json.output;
const discussionQuestions = aiDiscussionContent
  .split('\n')
  .filter(line => line.trim().length > 0)
  .map(line => line.replace(/^\d+[\.\)]\s*/, '').trim())
  .filter(line => line.length > 10)
  .slice(0, 5);

// Additional functions for data processing
function cleanTitle(title) {
  return title
    .replace(/^BBC Learning English - 6 Minute English \/ /, '')
    .replace(/^BBC Learning English - /, '')
    .trim();
}

function generateVocabHTML(vocabItems) {
  return vocabItems.map((item, index) => `
    <li class="vocab-item">
      <span class="vocab-word">${index + 1}. ${item.word}</span>
      <span class="vocab-definition">${item.definition}</span>
    </li>
  `).join('');
}

function generatePracticeHTML(exercises) {
  return exercises.map(ex => `<li>${ex.trim()}</li>`).join('');
}

function generateDiscussionHTML(questions) {
  return questions.map(q => `<li class="discussion-item">${q}</li>`).join('');
