// üß© Random CMC Listing Page Generator (sandbox-safe for n8n)
// Includes sort direction + pseudo page calculation

// --- CONFIG ---
const baseURL = "https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest";
const maxStart = 5000; // max offset
const limitOptions = [50, 100, 200];
const sortOptions = [
  "market_cap",
  "market_cap_strict",
  "name",
  "symbol",
  "date_added",
  "price",
  "circulating_supply",
  "total_supply",
  "max_supply",
  "num_market_pairs",
  "market_cap_by_total_supply_strict",
  "volume_24h",
  "volume_7d",
  "volume_30d",
  "percent_change_1h",
  "percent_change_24h",
  "percent_change_7d"
];
const sortDirs = ["asc", "desc"];

// --- HELPER ---
function randomChoice(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}
function toQuery(params) {
  return Object.entries(params)
    .map(([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v)}`)
    .join("&");
}

// --- RANDOMIZE ---
const limit = randomChoice(limitOptions);
const start = Math.floor(Math.random() * maxStart) + 1;
const sort = randomChoice(sortOptions);
const sort_dir = randomChoice(sortDirs);

// --- COMPOSE ---
const params = { start, limit, sort, sort_dir };
const query = toQuery(params);
const fullURL = `${baseURL}?${query}`;
const page = Math.ceil(start / limit);

// --- OUTPUT ---
return [
  {
    json: {
      description: "Randomized CoinMarketCap Listings URL",
      fullURL,
      params: { ...params, page }
    }
  }
];

