const allRows = $items("DATA").map(item => item.json);

// You can now inspect or return them
return allRows.map((row, index) => ({
  json: {
    index: index + 1,
    ...row,
  }
}));

function pickWebsite(v) {
  if (!v) return null;

  if (Array.isArray(v)) {
    v = v.find(x => typeof x === 'string' && x.trim()) || null;
  }

  if (typeof v === 'string') {
    let url = v.trim();

    // Handle stringified arrays: ["http://..."]
    if (url.startsWith('[') && url.endsWith(']')) {
      try {
        const parsed = JSON.parse(url);
        if (Array.isArray(parsed)) url = parsed[0];
      } catch {}
    }

    // Add protocol if missing
    if (!/^https?:\/\//i.test(url)) url = 'https://' + url;

    // Force HTTPS
    url = url.replace(/^http:\/\//i, 'https://');

    // Remove stray brackets/quotes
    url = url.replace(/["'\[\]]/g, '');

    return url;
  }

  return null;
}

const j = $json;

// Match your real field names
const out = {
  token: j.TOKEN || j.name || null,
  ticker: (j.TICKER || j.symbol || '').toString().toUpperCase(),
  source: j.SOURCE || 'COINGECKO',
  website: pickWebsite(j.WEBSITE || j.links?.homepage || j.homepage || null),
  timestamp: new Date().toISOString()
};

if (!out.website) return null;  // skip invalid
return out;
