// Get all rows from the previous Google Sheets (Read) node
const items = $items();

// If the sheet is empty, do nothing
if (items.length === 0) {
  return [{
    json: {
      message: 'No household budget data yet.'
    }
  }];
}

// Function to convert date strings "YYYY/MM/DD" or "YYYY-MM-DD" to Date objects
function parseDate(str) {
  if (!str) return null;
  const parts = str.split(/[\/\-]/).map(Number);
  if (parts.length < 3) return null;
  const [y, m, d] = parts;
  return new Date(y, m - 1, d);
}

// Assume the last row is the newly added record and aggregate by its month
const lastRow = items[items.length - 1].json;
const lastDateStr = lastRow['Date'];
const targetDate = parseDate(lastDateStr) || new Date();

// Target Year-Month (e.g., "2025-04")
const targetYm = `${targetDate.getFullYear()}-${('0' + (targetDate.getMonth() + 1)).slice(-2)}`;

// Calculate total for the current month
let total = 0;
for (const { json: row } of items) {
  const ds = row['Date'];
  const d = parseDate(ds);
  if (!d) continue;

  const ym = `${d.getFullYear()}-${('0' + (d.getMonth() + 1)).slice(-2)}`;
  if (ym === targetYm) {
    const price = Number(row['Amount'] || 0);
    if (!isNaN(price)) {
      total += price;
    }
  }
}

// ‚òÖ Monthly budget (change this to your desired amount)
const budget = 30000;

const remaining = budget - total;

// Assemble message for Slack
const lines = [
  `üìä Household Budget for ${targetYm}`,
  `Budget: ${budget.toLocaleString()} JPY`,
  `Total Spent: ${total.toLocaleString()} JPY`,
  `Remaining Budget: ${remaining.toLocaleString()} JPY`,
];

if (remaining < 0) {
  lines.push(`‚ö†Ô∏è You have exceeded your budget by ${Math.abs(remaining).toLocaleString()} JPY.`);
}

return [{
  json: {
    month: targetYm,
    budget: budget,
    totalSpent: total,
    remainingBudget: remaining,
    message: lines.join('\n')
  }
}];

// Assuming AI Agent output is in $json.output
let raw = $json.output;

// Convert to JSON if it's a string
if (typeof raw === 'string') {
  raw = raw.trim();

  // Remove ```json ~ ``` if present
  raw = raw.replace(/^```(?:json)?/i, '').replace(/```$/i, '').trim();

  try {
    raw = JSON.parse(raw);
  } catch (error) {
    throw new Error('Could not parse AI Agent output as JSON: ' + error.message + '\nraw=' + raw);
  }
}

// Ensure it's an array, even if only one item
const data = Array.isArray(raw) ? raw : [raw];

// Format into n8n's items array structure
const items = data.map(entry => {
  return {
    json: {
      Date: entry['Date'],
      Store: entry['Store'],
      Items: entry['Items'],
      Amount: entry['Amount']
    }
  };
});

return items;

