// All rows from the sheet
const items = $items();
if (items.length === 0) {
  return [{
    json: {
      message: 'No household budget data yet.'
    }
  }];
}

// Date parsing function
function parseDate(str) {
  if (!str) return null;
  const parts = str.split(/[\/\-]/).map(Number);
  if (parts.length < 3) return null;
  const [y, m, d] = parts;
  return new Date(y, m - 1, d);
}

// The month of the latest row's date will be the target month
const lastRow = items[items.length - 1].json;
const lastDate = parseDate(lastRow['Date']) || new Date();
const targetYm = `${lastDate.getFullYear()}-${('0' + (lastDate.getMonth() + 1)).slice(-2)}`;

// For aggregation
let total = 0;
const byStore = {};  // Per store
const byItem = {};   // Per item
const byDate = {};   // Per date

for (const { json: row } of items) {
  const d = parseDate(row['Date']);
  if (!d) continue;

  const ym = `${d.getFullYear()}-${('0' + (d.getMonth() + 1)).slice(-2)}`;
  if (ym !== targetYm) continue; // Skip if not current month

  const price = Number(row['Amount'] || 0);
  if (isNaN(price)) continue;

  const dateKey = row['Date'];
  const store = row['Store'] || 'Unknown Store';
  const item = row['Items'] || 'Unknown Item';

  total += price;

  byDate[dateKey] = (byDate[dateKey] || 0) + price;
  byStore[store] = (byStore[store] || 0) + price;
  byItem[item] = (byItem[item] || 0) + price;
}

// Daily average
const days = Object.keys(byDate).length || 1;
const avgPerDay = Math.round(total / days);

// Top N ranking function
function topN(map, n) {
  return Object.entries(map)
    .sort((a, b) => b[1] - a[1])
    .slice(0, n)
    .map(([key, value]) => ({
      name: key,
      amount: value
    }));
}

const topStores = topN(byStore, 3);
const topItems = topN(byItem, 3);
const topDays = topN(byDate, 3);

return [{
  json: {
    month: targetYm,
    monthlyTotal: total,
    dailyAverage: avgPerDay,
    top3Stores: topStores,
    top3Items: topItems,
    top3SpendingDays: topDays
  }
}];

