// Generate unique certificate ID and QR code
const items = $input.all();

return items.map(item => {
  // Generate unique certificate ID
  const timestamp = Date.now();
  const randomStr = Math.random().toString(36).substr(2, 9).toUpperCase();
  const certId = `CERT-${timestamp}-${randomStr}`;

  // Create verification URL
  const verifyUrl = `https://workshopverify.com/cert?id=${certId}`;

  // Generate QR code URL using free API
  const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(verifyUrl)}`;

  // Format date for display
  const formattedDate = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  return {
    json: {
      ...item.json,
      certificateId: certId,
      verifyUrl: verifyUrl,
      qrCodeUrl: qrCodeUrl,
      timestamp: new Date().toISOString(),
      formattedDate: formattedDate
    }
  };
});

