const results = [];

for (const item of $input.all()) {
  const url = item.json.URL_PARSE; // Input URL field

  try {
    // Fetch the raw HTML content
    const html = await this.helpers.httpRequest({
      method: 'GET',
      url,
      headers: {
        'User-Agent': 'Mozilla/5.0',
      },
    });

    // --- Extract title ---
    const titleMatch = html.match(/<title>(.*?)<\/title>/i);
    const title = titleMatch ? titleMatch[1].trim() : null;

    // --- Extract meta description ---
    const descMatch = html.match(/<meta\s+name=["']description["']\s+content=["']([^"']+)["']/i);
    const description = descMatch ? descMatch[1].trim() : null;

    // --- Extract all paragraphs (<p>...</p>) ---
    const paragraphs = [];
    const pRegex = /<p[^>]*>(.*?)<\/p>/gi;
    let match;
    while ((match = pRegex.exec(html)) !== null) {
      // Remove all HTML tags from paragraph text
      const cleanText = match[1].replace(/<[^>]+>/g, '').trim();
      if (cleanText) paragraphs.push(cleanText);
    }
    const text = paragraphs.join('\n\n');

    // --- Extract all <img> URLs ---
    const images = [];
    const imgRegex = /<img[^>]+src=["']([^"']+)["']/gi;
    let imgMatch;
    while ((imgMatch = imgRegex.exec(html)) !== null) {
      images.push(imgMatch[1]);
    }

    // --- Extract Open Graph metadata ---
    const ogTitleMatch = html.match(/<meta property=["']og:title["'] content=["']([^"']+)["']/i);
    const ogImageMatch = html.match(/<meta property=["']og:image["'] content=["']([^"']+)["']/i);
    const ogTitle = ogTitleMatch ? ogTitleMatch[1] : null;
    const ogImage = ogImageMatch ? ogImageMatch[1] : null;

    // --- Short summary (first 3 paragraphs) ---
    const summary = paragraphs.slice(0, 3).join('\n\n');

    // --- Add all extracted data to original item ---
    item.json.source_url = url;
    item.json.title = title;
    item.json.description = description;
    item.json.text = text;
    item.json.images = images;
    item.json.summary = summary;

  } catch (error) {
    // --- Handle errors ---
    item.json.source_url = url;
    item.json.title = null;
    item.json.description = null;
    item.json.text = null;
    item.json.images = [];
    item.json.summary = null;
  }

  results.push(item);
}

// Return updated items for downstream nodes
return results;
