// NOTE: We assume the Decodo search results are merged into the root 
// under the key 'results' (the large search result array).

const mergedItem = $input.item.json; 

// Path to the Decodo results: mergedItem.results[0].content.results
// We access the nested organic/paid arrays from this location:
const decodoContent = mergedItem.results?.[0]?.content?.results?.results || {}; 

const organicResults = Array.isArray(decodoContent.organic) ? decodoContent.organic : [];
const paidResults = Array.isArray(decodoContent.paid) ? decodoContent.paid : [];


const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;
const contactUrlRegex = /(contact|about|support)\-?us|imprint|privacy/i;

let foundEmail = '';
let contactPage = '';

// Function to check both paid and organic snippets for data
function extractDataFromResults(results) {
    if (!Array.isArray(results)) return;

    for (const result of results) {
        // The snippet text is in the 'desc' field
        const snippetText = result.desc || '';

        // 1. Extract Email
        const emailMatch = snippetText.match(emailRegex);
        if (emailMatch && !foundEmail) {
            foundEmail = emailMatch[0]; // Take the first one found
        }

        // 2. Extract Contact Page URL
        if (result.url && result.url.match(contactUrlRegex) && !contactPage) {
            contactPage = result.url; // Take the first relevant URL found
        }
    }
}

// Process results from both arrays
extractDataFromResults(organicResults);
extractDataFromResults(paidResults);

// Return a single object, merging the new contact info with the existing combined metadata
return {
    json: {
        ...mergedItem, // ‚¨ÖÔ∏è Preserves ALL metadata (lead_id, status, etc., and previous search results)
        contact_email: foundEmail, // ‚¨ÖÔ∏è Add new email (or keep it if empty)
        // Prefer new contact page, fall back to existing metadata links
        contact_page_url: contactPage
    }
};

// Function to convert Base64 string to a Uint8Array (pure JS replacement for Buffer.from(..., 'base64'))
function base64ToUint8Array(base64) {
    const binaryString = atob(base64);
    const len = binaryString.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) {
        bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes;
}

// Function to extract domain without using the URL constructor
function getDomain(fullUrl) {
    try {
        // Simple string manipulation to find and clean the domain
        const match = fullUrl.match(/^(?:https?:\/\/)?(?:[^@\n]+@)?(?:www\.)?([^:\/\n\?]+)/i);
        if (match && match[1]) {
            return match[1];
        }
        return '';
    } catch (e) {
        return '';
    }
}


const outputLeads = [{ source_url: '', source_title: '', domain: '', lead_type: '' }].filter(item => false);
const uniqueDomains = new Set();
const dataContainer = $input.first().json.results?.[0]?.content?.results?.results;

if (!dataContainer) {
    // Gracefully handle missing data structure
    return [{ json: { error: "Decodo: Expected data container not found." } }];
}


// Helper function to process and clean individual search results
const processResult = (item, type) => {
    const fullUrl = item.url || (item.snippet && item.snippet.link);
    const title = item.title || item.desc || (item.snippet && item.snippet.title);

    if (!fullUrl || !title) return;

    const domain = getDomain(fullUrl);

    if (domain && !uniqueDomains.has(domain)) {
        uniqueDomains.add(domain);
        outputLeads.push({
            source_url: fullUrl,
            source_title: title,
            domain: domain,
            lead_type: type
        });
    }
};


// Robustly check for and process array structures
const paidResults = Array.isArray(dataContainer.paid) ? dataContainer.paid : [];
for (const item of paidResults) {
    processResult(item, 'Paid Ad Lead');
}

const organicResults = Array.isArray(dataContainer.organic) ? dataContainer.organic : [];
for (const item of organicResults) {
    processResult(item, 'Organic Lead');
}

return outputLeads;

// Function to extract high-value emails using regex
function extractEmail(text) {
    // Regex for standard email format
    const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;
    const allEmails = text.match(emailRegex) || [];

    // Convert to a Set to ensure unique emails
    const uniqueEmails = [...new Set(allEmails)];

    let bestEmail = '';

    // 1. Prioritize High-Value Emails (Names, Sales, Founder)
    const priorityKeywords = /^(sales|contact|founder|ceo|director|manager|president|[a-z]{1})\W*\w*@/i;

    for (const email of uniqueEmails) {
        // Check if the part before the @ matches a priority keyword
        if (priorityKeywords.test(email.split('@')[0])) {
            bestEmail = email;
            break; 
        }
    }

    // 2. Fallback to Generic Emails (info, contact, support)
    if (!bestEmail) {
        const fallbackKeywords = /^(info|contact|support)/i;
        for (const email of uniqueEmails) {
            if (fallbackKeywords.test(email.split('@')[0])) {
                bestEmail = email;
                break;
            }
        }
    }

    // 3. Fallback to the first available unique email
    if (!bestEmail && uniqueEmails.length > 0) {
        bestEmail = uniqueEmails[0];
    }

    return bestEmail;
}

// Get the raw HTML/text output from the Decodo Scrape Contact Page node
// We assume the Decodo Scrape returns the content in the 'content' field for the single item
const rawContent = $input.item.json.results[0].content || '';

// The existing lead data (domain, lead_type, etc.) is passed through via $json
const finalEmail = extractEmail(rawContent);

return {
    json: {
        ...$json, // Keep previous data
        final_contact_email: finalEmail, // The highest-quality email found
        all_emails_found: finalEmail ? 1 : 0 // Simple count for verification
    }
};
