const html = $input.item.json.data || '';
const text = html.replace(/<script[^>]*>.*?<\/script>/gi, '').replace(/<style[^>]*>.*?<\/style>/gi, '').replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim().substring(0, 2000);

return {
  json: {
    site_text: text,
    title: $('Loop Over Items').item.json.title,
    categoryName: $('Loop Over Items').item.json.categoryName,
    website: $('Loop Over Items').item.json.website,
    address: $('Loop Over Items').item.json.address,
    phone: $('Loop Over Items').item.json.phone
  }
};
