parameters.inputSchema:
{
  "type": "array",
  "title": "SongList",
  "description": "An array of musical tracks extracted from a creative list.",
  "items": {
    "type": "object",
    "properties": {
      "track": {
        "type": "string",
        "description": "The title of the song, derived from the input list."
      },
      "artist": {
        "type": "string",
        "description": "The suggested artist for the song, based on the input name."
      }
    },
    "required": [
      "track",
      "artist"
    ]
  }
}
