// Get the HTML email body
const html = items[0].json.html_body;

// Helper function to strip HTML tags
function stripHtml(html) {
  return html
    .replace(/<br\s*\/?>/gi, "\n")
    .replace(/<\/p>/gi, "\n")
    .replace(/<\/div>/gi, "\n")
    .replace(/<\/tr>/gi, "\n")
    .replace(/<[^>]*>/g, "")
    .replace(/\n\s*\n/g, "\n")
    .trim();
}

const text = stripHtml(html);

// Extract fields using regex
const nameMatch = text.match(/Name:\s*(.*)/);
const emailMatch = text.match(/Email:\s*(.*)/);
const versionMatch = text.match(/Version:\s*(.*)/);

// Fallback if not found
const name = nameMatch ? nameMatch[1].trim() : "Not Available";
const email = emailMatch ? emailMatch[1].trim() : "Not Available";
const version = versionMatch ? versionMatch[1].trim() : "Not Available";

// Create Slack formatted message
const slackMessage = `
*New Form Submission Received* üéâ

*Name:* ${name}
*Email:* ${email}
*Version:* ${version}

The user has submitted the consent form query and their data has been safely recorded.
`;

return [
  {
    json: {
      text: slackMessage.trim()
    }
  }
];
