// === UNIVERSAL SAFE PARSER (STANDARDIZATION + UNIQUE ID) ===
const results = [];

for (const item of $input.all()) {
  const json = item.json || {};
  const html = json.html || json.data || json.content || "";
  const sourceUrl =
    $("Loop Through Sources (Rate Limit Safe)").first().json.source_url || "";
  let sourceType = $("Loop Through Sources (Rate Limit Safe)").first().json
    .source_type;
  let productName = $("Loop Through Sources (Rate Limit Safe)").first().json
    .product_name;

  if (!sourceType || sourceType === "unknown") {
    sourceType = detectSource(sourceUrl, html);
  }

  /** @type {any[]} */
  let reviews = [];
  let error = null;

  if (!html || html.length < 200) {
    error = "No HTML content";
  } else {
    try {
      if (sourceType === "trustpilot") reviews = parseTrustpilot(html);
      else if (sourceType === "capterra") reviews = parseCapterra(html);
      else if (sourceType === "chromewebstore")
        reviews = parseChromeWebStore(html);
      else if (sourceType === "producthunt") reviews = parseProductHunt(html);
      else if (sourceType === "trustradius") reviews = parseTrustRadius(html);
      else error = `Unknown source: ${sourceType}`;
    } catch (e) {
      error = e instanceof Error ? e.message : String(e);
      //error = e.message;
    }
  }

  // === STANDARDIZE EVERY REVIEW + ADD UNIQUE ID ===
  const standardizedReviews = (reviews || []).map((review) => {
    const cleanText = (review.text || "").trim();
    const cleanTitle = (review.title || "").trim();
    const author = (review.author || "Anonymous").trim();
    let date = null;
    if (review.date) {
      const d = new Date(review.date);
      // Check if 'd' is a valid date object
      if (!isNaN(d.getTime())) {
        try {
          date = d.toISOString().split("T")[0];
        } catch (e) {
          date = null; // Fallback if ISO conversion fails
        }
      } else {
        // Optional: If date is "2 days ago", it ends up here.
        // You can leave it null or map it to today's date if strictly necessary.
        date = null;
      }
    }

    // === UNIQUE ID: deterministic hash based on source + author + date + first 100 chars of text ===
    // This survives reformatting, extra spaces, etc. ‚Äî 99.99% duplicate-proof
    const hashString = `${sourceType}|${author}|${date}|${cleanText.substring(0, 150)}`;
    function simpleHash(str) {
      let hash = 0;
      for (let i = 0; i < str.length; i++) {
        const char = str.charCodeAt(i);
        hash = (hash << 5) - hash + char;
        hash = hash & hash; // Convert to 32-bit integer
      }
      return Math.abs(hash).toString(36); // short, URL-safe string
    }
    const unique_id = `${sourceType}_${simpleHash(hashString)}`;

    return {
      unique_id, // ‚Üê Your primary key / dedupe field
      author: author,
      rating: review.rating ? Number(review.rating) : null,
      title: cleanTitle || "[No title provided]",
      text: cleanText,
      pros: review.pros || "[Not specified]",
      cons: review.cons || "[Not specified]",
      date: date,
      job_title: review.job_title || "[Not provided]",
      company_size: review.company_size || "[Unknown]",
      industry: review.industry || "[Unknown]",
      location: review.location || "[Not specified]",
      verified: review.verified || false,

      scraped_at: new Date().toISOString(),
    };
  });

  const previousPage = $("URL Builder").first().json.page_number || 1;
  results.push({
    json: {
      //...json,
      // === CRITICAL: PASS METADATA FOR PAGINATION LOOP ===
      product_name: productName,
      page_number: previousPage,
      review_count: standardizedReviews.length,
      reviews: standardizedReviews,
      source_url: sourceUrl,
      source_type: sourceType,      
      success: !error,
      error_message: error,
    },
  });
}

  // === STEP 2: SAVE TO GLOBAL STORAGE ===
  const staticData = $getWorkflowStaticData('global');
  // Initialize if it doesn't exist (safety check)
  if (!staticData.allScrapedReviews) staticData.allScrapedReviews = [];

  // Append the results from this specific loop iteration to the global list
  // We map 'results' because it contains { json: ... } structure
  const cleanItems = results.map(r => r.json);
  staticData.allScrapedReviews = staticData.allScrapedReviews.concat(cleanItems);

return results;

// ==================== HELPER FUNCTIONS ====================

function detectSource(url, html) {
  const u = (url || "").toLowerCase();
  const h = html || "";

  // A. Check URL first
  if (u.includes("trustpilot")) return "trustpilot";
  if (u.includes("capterra")) return "capterra";
  if (u.includes("chrome.google.com") || u.includes("chromewebstore"))
    return "chromewebstore";
  if (u.includes("producthunt")) return "producthunt";
  if (u.includes("trustradius")) return "trustradius";

  // B. Check HTML Signatures (Fallback)
  if (
    h.includes("data-service-review-card-paper") ||
    h.includes("trustpilot-widget")
  )
    return "trustpilot";
  if (h.includes("__next_f") && h.includes("CapterraProduct"))
    return "capterra";
  if (h.includes("T7rvce") && h.includes("LfYwpe")) return "chromewebstore";
  if (
    h.includes("DetailedReviewConnection") ||
    h.includes("DetailedReviewEdge")
  )
    return "producthunt";
  if (h.includes("Review_review__") || h.includes("trScore"))
    return "trustradius";

  return "unknown";
}

function decodeHtml(html) {
  if (!html) return "";
  return html
    .replace(/&amp;/g, "&")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">")
    .replace(/&quot;/g, '"')
    .replace(/&#039;/g, "'")
    .replace(/<br\s*\/?>/gi, "\n");
}

// --- 1. Trustpilot Parser ---
function parseTrustpilot(html) {
  if (
    html.includes("cf-browser-verification") ||
    html.includes("Just a moment")
  ) {
    throw new Error("Blocked by Cloudflare.");
  }
  const cards = html.split('data-service-review-card-paper="true"').slice(1);
  const extracted = [];

  for (const card of cards) {
    const authorMatch = card.match(
      /data-consumer-name-typography="true">([^<]+)</,
    );
    const ratingMatch = card.match(/Rated (\d) out of 5/);
    const titleMatch = card.match(
      /data-service-review-title-typography="true">([^<]+)</,
    );
    const textMatch = card.match(
      /data-service-review-text-typography="true">([\s\S]*?)(?=<\/p>|<div|$)/,
    );
    const dateMatch = card.match(/datetime="([^"]+)"/);
    const locationMatch = card.match(
      /data-consumer-country-typography="true">([^<]+)</,
    );

    if (authorMatch && textMatch) {
      extracted.push({
        author: authorMatch[1].trim(),
        rating: ratingMatch ? parseInt(ratingMatch[1]) : null,
        title: titleMatch ? titleMatch[1].trim() : null,
        text: decodeHtml(textMatch[1]).trim(),
        date: dateMatch ? dateMatch[1] : null,
        location: locationMatch ? locationMatch[1].trim() : null,
        verified: card.includes("Verified"),
        source: "trustpilot",
      });
    }
  }
  return extracted;
}

// --- 2. Capterra Parser ---
function parseCapterra(html) {
  let fullDataString = "";
  const regex = /<script>self\.__next_f\.push\(\[1,"([\s\S]*?)"\]\)<\/script>/g;
  let match;
  while ((match = regex.exec(html)) !== null) {
    fullDataString += match[1];
  }

  if (!fullDataString) {
    throw new Error("No Next.js data script tags found.");
  }

  // C. Build Data Map (Your Split Logic)
  const dataMap = new Map();
  // In the regex capture, newlines are stored as the literal string "\\n"
  const chunks = fullDataString.split("\\n");

  chunks.forEach((chunk) => {
    if (!chunk) return;
    const firstColonIndex = chunk.indexOf(":");
    if (firstColonIndex === -1) return;

    const key = chunk.substring(0, firstColonIndex);
    let value = chunk.substring(firstColonIndex + 1);
    // Unescape quotes *after* splitting
    value = value.replace(/\\"/g, '"');
    dataMap.set(key, value);
  });

  if (dataMap.size === 0)
    throw new Error("Data map empty. Regex succeeded but split failed.");

  // D. Recursive Resolver (Your Cache Logic)
  const resolvedCache = new Map();
  function resolve(key) {
    if (resolvedCache.has(key)) return resolvedCache.get(key);

    let valueStr = dataMap.get(key);
    if (!valueStr) return null;

    let result;
    if (valueStr.startsWith("{") || valueStr.startsWith("[")) {
      try {
        result = JSON.parse(valueStr, (reviverKey, reviverValue) => {
          if (
            typeof reviverValue === "string" &&
            reviverValue.startsWith("$")
          ) {
            const refKey = reviverValue.substring(1);
            return resolve(refKey);
          }
          return reviverValue;
        });
      } catch (e) {
        result = null;
      }
    } else if (valueStr.startsWith("$")) {
      result = resolve(valueStr.substring(1));
    } else if (valueStr.startsWith('"') && valueStr.endsWith('"')) {
      result = valueStr.substring(1, valueStr.length - 1);
    } else {
      result = valueStr;
    }
    resolvedCache.set(key, result);
    return result;
  }

  // E. Find Main Reviews Key (Your Key Finder)
  let mainReviewsKey = null;
  for (const [key, value] of dataMap.entries()) {
    if (
      value.includes('"__typename":"CapterraProduct"') &&
      value.includes('"textReviews":')
    ) {
      const reviewKeyMatch = value.match(
        /"textReviews":"\$([a-zA-Z0-9]{1,5})"/,
      );
      if (reviewKeyMatch && reviewKeyMatch[1]) {
        mainReviewsKey = reviewKeyMatch[1];
        break;
      }
    }
  }

  // Fallback strategy if Product Object search fails
  if (!mainReviewsKey) {
    for (const [key, value] of dataMap.entries()) {
      if (value.startsWith('["$') && value.endsWith('"]')) {
        // Basic heuristic: assume the first array of references might be it
        mainReviewsKey = key;
        break;
      }
    }
  }

  if (!mainReviewsKey) throw new Error("Could not find main review list key.");

  // F. Resolve List
  const mainReviewsList = resolve(mainReviewsKey);
  if (!Array.isArray(mainReviewsList))
    throw new Error("Resolved review list is not an array.");

  // G. Map Data (Your Mapper)
  const getRating = (rating) =>
    rating !== null && rating !== undefined
      ? parseFloat(rating).toString()
      : null;

  return mainReviewsList
    .map((review) => {
      if (!review || !review.reviewer) return null;

      let formattedDate = review.writtenOn; // Keep original if parsing fails
      try {
        // Simple date formatting
        formattedDate = new Date(review.writtenOn).toISOString().split("T")[0];
      } catch (e) {}

      return {
        author: review.reviewer.anonymityOn
          ? "Verified Reviewer"
          : review.reviewer.fullName,
        job_title: review.reviewer.jobTitle,
        company_size: review.reviewer.companySize,
        industry: review.reviewer.industry,
        rating: getRating(review.overallRating),
        title: review.title,
        text: review.generalComments,
        pros: review.prosText,
        cons: review.consText,
        date: formattedDate,
        verified: review.reviewer.isValidated ? "Yes" : "No",
        source: "capterra",
      };
    })
    .filter((r) => r !== null);
}

// --- 3. Chrome Web Store Parser ---
function parseChromeWebStore(html) {
  const chunks = html.split('class="T7rvce"').slice(1);
  const extracted = [];
  for (const chunk of chunks) {
    const authorMatch = chunk.match(/class="LfYwpe">([^<]+)</);
    const ratingMatch = chunk.match(/aria-label="(\d) out of 5 stars"/);
    const dateMatch = chunk.match(/class="ydlbEf">([^<]+)</);
    const textMatch = chunk.match(/class="fzDEpf"[^>]*>([\s\S]*?)<\/p>/);

    if (authorMatch) {
      extracted.push({
        author: authorMatch[1].trim(),
        rating: ratingMatch ? parseInt(ratingMatch[1]) : null,
        date: dateMatch ? dateMatch[1].trim() : null,
        text: textMatch ? decodeHtml(textMatch[1]).trim() : null,
        source: "chromewebstore",
      });
    }
  }
  return extracted;
}

// --- 4. Product Hunt Parser ---
function parseProductHunt(html) {
  const extracted = [];
  const seenIds = new Set();

  const startMatch = html.match(/DetailedReviewConnection.*?edges/);
  if (!startMatch) return []; // Return empty if not found

  const section = html.substring(startMatch.index);
  const blocks = section.split(/DetailedReviewEdge.*?node.*?DetailedReview/);
  blocks.shift();

  const getVal = (block, key) => {
    const r = new RegExp(`"${key}":"([^"]+)"`);
    const m = block.match(r);
    return m ? m[1] : null;
  };

  for (const block of blocks) {
    const reviewId = getVal(block, "id");
    if (reviewId && seenIds.has(reviewId)) {
      continue;
    }
    if (reviewId) seenIds.add(reviewId);

    const name = getVal(block, "name") || "Anonymous";
    const body = getVal(block, "body") || getVal(block, "overallExperience");
    const date = getVal(block, "createdAt");

    if (body) {
      extracted.push({
        unique_id: `producthunt_${reviewId}`,
        author: name,
        text: decodeHtml(body.replace(/\\n/g, "\n")),
        date: date,
        source: "producthunt",
      });
    }
  }
  return extracted;
}

// --- 5. TrustRadius Parser ---
function parseTrustRadius(html) {
  const blocks = html.split('<article class="Review_review');
  blocks.shift();
  const extracted = [];
  for (const block of blocks) {
    const titleMatch = block.match(
      /Header_heading__[^>]+><a[^>]+>([^<]+)<\/a>/,
    );
    const ratingMatch = block.match(/data-rating="([\d\.]+)"/);
    const nameMatch = block.match(/Byline_name__[^>]+>([^<]+)<\/div>/);
    const dateMatch = block.match(/datetime="([^"]+)"/);
    const summaryMatch = block.match(
      /Use Cases and Deployment Scope<\/h3><p[^>]*>(.*?)<\/p>/,
    );

    if (ratingMatch) {
      extracted.push({
        author: nameMatch ? decodeHtml(nameMatch[1]) : "Anonymous",
        rating: parseFloat(ratingMatch[1]),
        title: titleMatch ? decodeHtml(titleMatch[1]) : null,
        text: summaryMatch ? decodeHtml(summaryMatch[1]) : null,
        date: dateMatch ? dateMatch[1] : null,
        source: "trustradius",
      });
    }
  }
  return extracted;
}

// === DYNAMIC URL BUILDER ===
const item = $input.first().json;

// Get the current page number from the loop context, default to 1
const page = item.page_number || 1;
let url = item.source_url;
const type = item.source_type;

if (!url) {
  throw new Error("Pagination Error: 'source_url' was lost in the loop.");
}

// 1. Chrome Web Store: Skip pagination (Infinite Scroll logic is too complex for this method)
if (type === 'chromewebstore') {
  return { ...item, target_url: url };
}

// 2. Pagination Logic
// If we are on Page 1, we usually don't need the parameter (safer to avoid redirects)

if (page > 1) {
  // If ProductHunt already has params (like ?feed=single), this correctly uses '&'
  const separator = url.includes('?') ? '&' : '?';
  url = `${url}${separator}page=${page}`;
}

return { 
  ...item, 
  target_url: url 
};

// === 1. Reset global storage for scraped reviews ===
const staticData = $getWorkflowStaticData('global');
staticData.allScrapedReviews = [];

// === 2. Hard-coded defaults (your fallback) ===
const defaultProduct = {
    name: "Thordata",
    trustpilot_domain: "thordata.com",
    capterra_url: "",
    chrome_webstore_url: "",
    trustradius_url: "",
    producthunt_slug: ""
};

// === 3. Start with defaults ===
let product = { ...defaultProduct };

// === 4. Override with Form input (highest priority) ===
const formData = $input.first()?.json;

if (formData && (
    formData.productName || 
    formData.domain || 
    formData.capterraUrl || 
    formData.chromeStoreUrl || 
    formData.trustRadius || 
    formData.producthunt
)) {
    product.name              = (formData.productName || defaultProduct.name)?.trim() || defaultProduct.name;
    product.trustpilot_domain = (formData.domain || defaultProduct.trustpilot_domain)?.trim() || defaultProduct.trustpilot_domain;
    product.capterra_url      = (formData.capterraUrl || defaultProduct.capterra_url)?.trim() || "";
    product.chrome_webstore_url = (formData.chromeStoreUrl || defaultProduct.chrome_webstore_url)?.trim() || "";
    product.trustradius_url   = (formData.trustRadius || defaultProduct.trustradius_url)?.trim() || "";
    product.producthunt_slug  = (formData.producthunt || defaultProduct.producthunt_slug)?.trim() || "";
}

// === 5. Override with Webhook POST body (if present) ===
else if ($node["Webhook Trigger"] && $input.first()?.json && Object.keys($input.first().json).length > 0) {
    const body = $input.first().json;

    product.name              = (body.productName || defaultProduct.name)?.trim() || defaultProduct.name;
    product.trustpilot_domain = (body.domain || defaultProduct.trustpilot_domain)?.trim() || defaultProduct.trustpilot_domain;
    product.capterra_url      = (body.capterraUrl || "").toString().trim() || "";
    product.chrome_webstore_url = (body.chromeStoreUrl || "").toString().trim() || "";
    product.trustradius_url   = (body.trustRadius || "").toString().trim() || "";
    product.producthunt_slug  = (body.producthunt || defaultProduct.producthunt_slug)?.trim() || "";
}

// (Manual ‚ÄúExecute Workflow‚Äù button ‚Üí uses defaults only)

// === 6. Build the standardized sources array ===
const sources = [];

if (product.trustpilot_domain) {
    sources.push({
        product_name: product.name,
        source_type: "trustpilot",
        source_url: "https://www.trustpilot.com/review/" + product.trustpilot_domain
    });
}

if (product.capterra_url) {
    sources.push({
        product_name: product.name,
        source_type: "capterra",
        source_url: product.capterra_url
    });
}

if (product.chrome_webstore_url) {
    sources.push({
        product_name: product.name,
        source_type: "chromewebstore",
        source_url: product.chrome_webstore_url
    });
}

if (product.trustradius_url) {
    sources.push({
        product_name: product.name,
        source_type: "trustradius",
        source_url: product.trustradius_url
    });
}

if (product.producthunt_slug) {
    sources.push({
        product_name: product.name,
        source_type: "producthunt",
        source_url: "https://www.producthunt.com/products/" + product.producthunt_slug + "/reviews?feed=single&filter=all&order=newest"
    });
}

// === 7. Return one item per source ===
return sources.map(source => ({ json: source }));

// === STEP 3: READ GLOBAL STORAGE ===
const staticData = $getWorkflowStaticData('global');
const allItems = staticData.allScrapedReviews || [];

const allReviews = [];
const errors = [];
const seenIds = new Set();       // ‚Üê Extra safety net
const dedupedReviews = [];

allItems.forEach(item => {
  if (!item) return [];
  const json = item.json || item;
  if (!json) return [];

  const { reviews = [], success, error_message, source_detected, source_url } = json;

  if (!success) {
    errors.push({ source: source_detected || source_url, error: error_message });
  } else {
  // Ensure reviews is actually an array before looping
    if (Array.isArray(reviews)) {  
    reviews.forEach(r => {
      if (!seenIds.has(r.unique_id)) {
        seenIds.add(r.unique_id);
        dedupedReviews.push(r);
      }
    });
  }
  }
});

return [{
  json: {
    summary: {
      product: "Thordata",
      total_reviews_scraped: dedupedReviews.length,
      unique_reviews: dedupedReviews.length,
      sources_attempted: $input.all().length,
      sources_failed: errors.length,
      scraped_at: new Date().toISOString()
    },
    reviews: dedupedReviews,
    errors: errors.length > 0 ? errors : null
  }
}];

const { summary, reviews } = $input.first().json;

// Format reviews into readable text
const reviewText = reviews.map((r, idx) => {
  return `Review #${idx + 1} (${r.source} - Rating: ${r.rating}/5):
Author: ${r.author || 'Anonymous'}
Date: ${r.date}
${r.title ? 'Title: ' + r.title : ''}
Text: ${r.text || '[No text provided]'}`;
}).join('\n\n---\n\n');

// Calculate rating distribution
const ratings = reviews.map(r => r.rating).filter(r => r);
const avgRating = ratings.length > 0 
  ? (ratings.reduce((a, b) => a + b, 0) / ratings.length).toFixed(2) 
  : 0;

const ratingDist = {
  '5': ratings.filter(r => r === 5).length,
  '4': ratings.filter(r => r === 4).length,
  '3': ratings.filter(r => r === 3).length,
  '2': ratings.filter(r => r === 2).length,
  '1': ratings.filter(r => r === 1).length
};

// Sources breakdown
const sources = [...new Set(reviews.map(r => r.source))];

return [{
  json: {
    // Flattened for template
    product: summary.product,
    total_reviews: summary.unique_reviews,
    avg_rating: avgRating,

    // For the prompt
    all_reviews_text: reviewText,

    // Additional context
    rating_distribution: ratingDist,
    sources: sources,
    date_range: {
      earliest: reviews.map(r => r.date).sort()[0],
      latest: reviews.map(r => r.date).sort().reverse()[0]
    },

    // Keep original for reference
    original_reviews: reviews,
    original_summary: summary
  }
}];

