// === 1. Get the AI raw JSON string ===
const openAiOutput = $input.first().json.output?.[0]?.content?.[0]?.text;

if (!openAiOutput) {
  throw new Error("OpenAI response missing");
}

let aiAnalysis;
try {
  aiAnalysis = JSON.parse(openAiOutput);
} catch (e) {
  throw new Error(`Failed to parse AI response as JSON:\n${openAiOutput}`);
}

// Now safe to use
const product = $('Prepare Data for AI Analysis').first().json.product || "Unknown Product";
const totalReviews = $('Prepare Data for AI Analysis').first().json.total_reviews || 0;
const avgRating = $('Prepare Data for AI Analysis').first().json.avg_rating ? Number($('Prepare Data for AI Analysis').first().json.avg_rating).toFixed(2) : "N/A";

// === 3. Colors ===
const sentimentColor = {
  positive: "#10b981",
  neutral: "#f59e0b",
  negative: "#ef4444"
}[aiAnalysis.collective_sentiment_label] || "#6b7280";

