// Get the incoming data from the previous node.
// n8n provides this as the '$json' variable.
const incomingData = $json;

// Create the final, perfectly structured payload for the Facebook CAPI node.
const capiPayload = {
  data: [
    {
      event_name: incomingData.event_name,
      event_time: incomingData.conversion_timestamp,
      action_source: 'website',

      // Build the user_data object by mapping your fields to Meta's fields.
      user_data: {
        // --- These are the two lines we are fixing ---
        // We are now 100% sure they match your input data.
        fn: incomingData.hashed_firstName,
        ln: incomingData.hashed_lastName,

        // The rest of the mapping
        em: incomingData.hashed_em,
        ph: incomingData.hashed_ph,
        fbc: incomingData.fbc,
        fbp: incomingData.fbp,
        client_ip_address: incomingData.ipAddress,
        client_user_agent: incomingData.userAgent,
      },
    },
  ],
};

// Return the transformed data so the next node can use it.
return capiPayload;
