// ---------------- Config (topic-aware) ----------------

// Tje Keywords filtering - Replace keywords as you want.
const KEEP_CRYPTO = [
  'crypto','cryptocurrency','bitcoin','btc','ethereum','eth','market','markets','signals',
  'selloff','sell-off','dump','dumped','liquidation','flash crash','$Grass','$grass','grass token',
  'bullish','bearish','breakout','btcusd','etf','spot etf','hack','hacked','hacking','exploit','rug pull','rug',
];

const KEEP_STOCKS = [
  'stock','stocks','equities','market','markets','earnings','guidance','revenue','profit','loss',
  'ipo','dividend','split','buyback','upgrade','downgrade','rating','price target','outlook','fomc',
  'dow','nasdaq','s&p','spy','qqq',
  // common tickers / names
  'nvda','nvidia','aapl','apple','tsla','tesla','msft','microsoft','amzn','amazon','meta','facebook','goog','googl','alphabet',
];

const DROP = ['giveaway','airdrop','referral','signal group','win $','win$'];
const MAX_IMAGES = 4;

// set true only while debugging to clear dedupe memory for this run
const CLEAR_MEMORY_THIS_RUN = false;

// ---------------- Helpers ----------------
const wf = $getWorkflowStaticData('global');
if (CLEAR_MEMORY_THIS_RUN) wf.seen = [];
const seen = new Set(wf.seen || []);

const esc = (s) => s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

const buildKeepRE = (arr) =>
  arr.map((k) => {
    const pat = esc(k).replace(/\\\s+/g, '\\s+');     // "spot etf" -> spot\s+etf
    return [k, new RegExp(`(^|[^\\w])${pat}([^\\w]|$)`, 'i')];
  });

const KEEP_RE_CRYPTO = buildKeepRE(KEEP_CRYPTO);
const KEEP_RE_STOCKS = buildKeepRE(KEEP_STOCKS);
const DROP_RE = DROP.map((k) => {
  const pat = esc(k).replace(/\\\s+/g, '\\s+');
  return new RegExp(`(^|[^\\w])${pat}([^\\w]|$)`, 'i');
});

const normSource = (j, url) => {
  if (j.source) return j.source;
  const u = (url || '').toLowerCase();
  if (u.includes('coindesk')) return 'CoinDesk';
  if (u.includes('cointelegraph')) return 'CoinTelegraph';
  if (u.includes('news.google')) return 'Google News';
  if (u.includes('xcancel') || u.includes('/status/')) return 'X posts';
  return 'rss';
};

const kindOf = (j, url) => {
  const src = (j.source || '').toLowerCase();
  const u = (url || '').toLowerCase();
  if (src.includes('x') || src.includes('tweet') || u.includes('/status/')) return 'tweet';
  return 'article';
};

const extractImages = (html = '', enclosure) => {
  const out = [];
  if (enclosure?.url) out.push(enclosure.url);
  const re = /<img[^>]+src=["']([^"']+)["']/ig;
  let m;
  while ((m = re.exec(html)) && out.length < MAX_IMAGES) out.push(m[1]);
  return [...new Set(out)];
};

const canonicalFromGoogleNews = (item) => {
  const m = /<a[^>]+href=["']([^"']+)["']/i.exec(item.content || '');
  return m ? m[1] : (item.link || item.guid || '');
};

const canonicalUrl = (j) => {
  let u = j.link || j.guid || j.url || '';
  if ((u || '').includes('news.google.com')) u = canonicalFromGoogleNews(j);
  return u.replace(/^https?:\/\/(www\.)?/i, '').replace(/#.*$/,'').trim();
};

const textOf = (j) => {
  const raw = [j.title, j.contentSnippet, j.content].filter(Boolean).join(' ');
  return raw.replace(/[\s\u00A0]+/g, ' ').replace(/[#$]/g, ' ').trim();
};

const matchedKeywords = (txt, keepList) => {
  const hits = [];
  for (const [label, re] of keepList) if (re.test(txt)) hits.push(label);
  return [...new Set(hits)];
};

const isDropped = (txt) => DROP_RE.some((re) => re.test(txt));

// --------- NEW: derive run-level topic & repair each item ----------
const fromInit = $item(0)?.$node?.["Init RunConfig"]?.json?.topic;
const RUN_TOPIC = (() => {
  const raw = String($json.topic ?? fromInit ?? 'crypto').toLowerCase().trim();
  if (raw === 'stocks' || raw === 'stock') return 'stocks';
  return 'crypto';
})();

// make sure every incoming item has a good topic (fixes "=" or missing)
const input = $input.all().map(i => {
  const t = String(i.json.topic ?? '').toLowerCase().trim();
  const fixedTopic = t && t !== '=' ? t : RUN_TOPIC;
  return { ...i.json, topic: fixedTopic };
});

// ---------------- Main ----------------
const counts = { in: input.length, kept: 0, dropped: 0, perSource: {} };
const out = [];

for (const j of input) {
  const topic = String(j.topic || RUN_TOPIC).toLowerCase();
  const KEEP_RE = topic === 'stocks' ? KEEP_RE_STOCKS : KEEP_RE_CRYPTO;

  const urlRaw = j.link || j.guid || j.url || '';
  const url = (urlRaw || '').trim();
  const title = (j.title || '').trim();
  if (!url || !title) { counts.dropped++; continue; }

  const text = textOf(j);
  if (!text) { counts.dropped++; continue; }

  if (isDropped(text)) { counts.dropped++; continue; }

  const hits = matchedKeywords(text, KEEP_RE);
  if (!hits.length) { counts.dropped++; continue; }

  const keyOnly = canonicalUrl(j);
  if (!keyOnly) { counts.dropped++; continue; }

  // Deduplicate across runs, per topic
  const key = `${topic}:${keyOnly}`;
  if (seen.has(key)) { counts.dropped++; continue; }
  seen.add(key);

  const when = new Date(j.isoDate || j.pubDate || j.publishedAt || Date.now());

  const src = normSource(j, url);
  counts.perSource[src] = (counts.perSource[src] || 0) + 1;

  out.push({
    json: {
      id: url,
      source: src,
      kind: kindOf(j, url),
      title,
      url,
      publishedAt: when.toISOString(),
      matchedKeywords: hits.join(','),    // same field as before
      summary: (j.contentSnippet || '').replace(/\s+/g, ' ').trim(),
      html: j.content || '',
      media: extractImages(j.content, j.enclosure),
      topic,                               // ensured & sanitized
    },
  });
}

counts.kept = out.length;
console.log({ stats: counts });

// keep some memory (cap length)
wf.seen = Array.from(seen).slice(-500);

return out;

