const mem = $getWorkflowStaticData('global');

// all RSS items produced for this batch
const batch = $input.all().map(i => i.json);

// append to the accumulator
mem.x_items = (mem.x_items ?? []).concat(batch);

// read the metadata written by “Set batch metadata”
const batchIndex  = Number(mem.batchIndex ?? 0);
const batchCount  = Number(mem.batchCount ?? 0);
const hasMore     = batchIndex < (batchCount - 1);

// emit a single control item that drives the IF node
