  json: {
    added: batch.length,
    total: mem.x_items.length,
    batchIndex,
    batchCount,
    hasMore,
  },
}];

// Build Xcancel URLs (batched, short queries)
// topic comes from Init RunConfig (crypto | stocks)
// optional: $json.tickers = ["NVDA","AAPL",...] when topic === 'stocks'

const topic = String($json.topic ?? 'crypto').toLowerCase();

// ----- Tunables you can tweak -----
const MAX_TICKERS_PER = 4;      // <= keep small
const MAX_NEWS_PER    = 3;      // <= keep small
const MAX_PER_BATCH_CRYPTO = 6; // crypto keywords per query
const MIN_FAVES = 20;
const MIN_RTS   = 5;
// Optional length guard for extra safety (after quoting, before encoding)
const MAX_QUERY_CHARS = 420;
// ----------------------------------

const filters =
  `lang:en -is:retweet -is:reply -is:quote filter:links min_faves:${MIN_FAVES} min_retweets:${MIN_RTS}`;

const toOR = (terms) =>
  '(' +
  terms
    .filter(Boolean)
    // quote if it has a space, a $ (cashtag), or any non-word char (e.g. M&A, S&P)
    .map((t) => (/[^\w]/.test(t) ? `"${t}"` : t))
    .join(' OR ') +
  ')';

const toUrl = (q) =>
  'https://xcancel.com/search/rss?f=tweets&q=' +
  encodeURIComponent(q.replace(/\s+/g, ' ').trim());

const chunk = (arr, size) => {
  const out = [];
  for (let i = 0; i < arr.length; i += size) out.push(arr.slice(i, i + size));
  return out;
};

let batches = [];

if (topic === 'stocks') {
  // ----- STOCKS -----
  const tickers = Array.isArray($json.tickers) && $json.tickers.length
    ? $json.tickers
    : [
        'AAPL','MSFT','NVDA','AMZN','GOOGL','META','TSLA','AMD','AVGO',
        'NFLX','TSM','JPM','BAC','WMT','ORCL',
      ];
  const cashtags = tickers.map((t) => `$${String(t).toUpperCase()}`);

  const news = [
    'earnings','EPS','guidance','revenue','outlook','forecast',
    'upgrade','downgrade','price target','PT',
    'dividend','buyback','merger','acquisition','M&A','IPO',
    'halt','after hours','pre-market',
  ];

  const cashtagGroups = chunk(cashtags, MAX_TICKERS_PER);
  const newsGroups    = chunk(news,    MAX_NEWS_PER);

  // Pair small cashtag groups with small news groups: (tickers) AND (news)
  // This keeps each query short and highly relevant.
  for (const tg of cashtagGroups) {
    for (const ng of newsGroups) {
      let q = `${toOR(tg)} ${toOR(ng)} ${filters}`;
      // Safety: if still long, split news group to singles
      if (q.length > MAX_QUERY_CHARS) {
        for (const single of ng) {
          const q2 = `${toOR(tg)} ${toOR([single])} ${filters}`;
          batches.push({ rssUrl: toUrl(q2), queryReadable: q2 });
        }
        continue;
      }
      batches.push({ rssUrl: toUrl(q), queryReadable: q });
    }
  }

  // (Optional) add a very small set of pure-market queries (no cashtag) if you like:
  // for (const ng of newsGroups) {
  //   const q = `${toOR(ng)} (${['stocks','equities','market'].join(' OR ')}) ${filters}`;
  //   batches.push({ rssUrl: toUrl(q), queryReadable: q });
  // }
} else {
  // ----- CRYPTO -----
  const cryptoTerms = [
    'bitcoin','btc','spot ETF','flash crash','liquidation','btcusd','breakout',
    'ethereum','eth','etf','rug pull','hack','hacked','exploit','bullish','bearish',
    'Grass token','$Grass',
  ];

  for (const group of chunk(cryptoTerms, MAX_PER_BATCH_CRYPTO)) {
    const q = `${toOR(group)} ${filters}`;
    batches.push({ rssUrl: toUrl(q), queryReadable: q });
  }
}

// annotate with batch indexes
batches = batches.map((b, i) => ({
  ...b,
  batchIndex: i,
  batchCount: batches.length,
  topic,
}));

return batches.map((b) => ({ json: b }));

