// Nimmt das erste Item aus dem Input
const item = $input.item;

// Hier liegt dein KI-Output als Text, z.B.:
// output
// { "thread": [ "Tweet 1", "Tweet 2", ... ] }
let aiOutput = item.json.output;

if (!aiOutput) {
	throw new Error('Kein Feld "output" im JSON gefunden.');
}

// In manchen F√§llen h√§ngt vor dem JSON noch "output" o.√§. oder ```json-Markdown.
// 1) Code-Fences entfernen
aiOutput = aiOutput
	.replace(/```json/gi, '')
	.replace(/```/g, '')
	.trim();

// 2) Nur den JSON-Teil extrahieren: erst '{', dann letztes '}'
const firstBrace = aiOutput.indexOf('{');
const lastBrace = aiOutput.lastIndexOf('}');

if (firstBrace === -1 || lastBrace === -1) {
	throw new Error('Konnte kein g√ºltiges JSON in "output" finden.');
}

let jsonString = aiOutput.slice(firstBrace, lastBrace + 1).trim();

let parsed;
try {
	parsed = JSON.parse(jsonString);
} catch (error) {
	throw new Error('Fehler beim JSON.parse: ' + error.message + '\nJSON-String: ' + jsonString);
}

// Erwartete Struktur: { "thread": [ "...", "...", ... ] }
const thread = Array.isArray(parsed.thread) ? parsed.thread : [];

// 5 Tweets extrahieren (falls weniger vorhanden sind, leere Strings)
const tweet1 = thread[0] || '';
const tweet2 = thread[1] || '';
const tweet3 = thread[2] || '';
const tweet4 = thread[3] || '';
const tweet5 = thread[4] || '';

// Optional: Array aller Tweets
const tweetsArray = [tweet1, tweet2, tweet3, tweet4, tweet5];

// Item zur√ºckgeben (n8n erwartet ein Array von Items)
return [
	{
		json: {
			// Originaldaten beibehalten
			...item.json,
			// Neue Felder
			tweet1,
			tweet2,
			tweet3,
			tweet4,
			tweet5,
			tweetsArray,
		},
	},
];

