// Get the output from AI Agent
const aiOutput = $input.item.json.output;

if (!aiOutput) {
  throw new Error('No output found from AI Agent');
}

// Try to extract JSON from the text
// The JSON might be wrapped in markdown code blocks or mixed with other text
let jsonString = aiOutput;

// Remove markdown code blocks if present
jsonString = jsonString.replace(/```json\s*/g, '').replace(/```\s*/g, '');

// Try to find JSON object in the text
const jsonMatch = jsonString.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  jsonString = jsonMatch[0];
}

try {
  // Parse the JSON
  const parsed = JSON.parse(jsonString);

  // Validate that we have the required fields
  if (!parsed.videoPrompt || !parsed.socialMediaText) {
    throw new Error('Missing required fields: videoPrompt or socialMediaText');
  }

  // Return the parsed data
  return {
    videoPrompt: parsed.videoPrompt,
    socialMediaText: parsed.socialMediaText
  };

} catch (error) {
  // If parsing fails, try to extract the fields manually
  const videoPromptMatch = aiOutput.match(/videoPrompt["']?\s*:\s*["']([^"']+)["']/i);
  const socialMediaTextMatch = aiOutput.match(/socialMediaText["']?\s*:\s*["']([^"']+)["']/i);

  if (videoPromptMatch && socialMediaTextMatch) {
    return {
      videoPrompt: videoPromptMatch[1],
      socialMediaText: socialMediaTextMatch[1]
    };
  }

  throw new Error(`Failed to parse AI output: ${error.message}. Output was: ${aiOutput}`);
}

