// Get data from previous nodes
// Note: Ensure your input nodes are named 'Watchlist' and 'Preferences'
const watchlistItems = $('Watchlist').all();
const prefItems = $('Preferences').all();

// 1. Extract Persona & Language
// Finds the row where Key is 'Persona' or 'Language'
const persona = prefItems.find(i => i.json.Key === 'Persona')?.json.Value || "A helpful assistant";
const language = prefItems.find(i => i.json.Key === 'Language')?.json.Value || "English";

// 2. Sort Ingredients into Categories
// We only want 'Active' items
const activeItems = watchlistItems.filter(i => i.json.Status === 'Active');

const badStuff = activeItems
  .filter(i => i.json.Type === 'Bad Stuff')
  .map(i => i.json['Ingredient Name'])
  .join(", ");

const goodStuff = activeItems
  .filter(i => i.json.Type === 'Good Stuff')
  .map(i => i.json['Ingredient Name'])
  .join(", ");

// 3. Return Clean Data for the AI
return {
  json: {
    persona: persona,
    target_language: language,
    bad_list: badStuff,
    good_list: goodStuff
  }
};

// The AI Agent node places the final text in the 'output' field
const content = $input.item.json.output;

// Logic to find the JSON object within the text (in case the AI adds chatter)
// This regex looks for content between { and }
const jsonMatch = content.match(/\{[\s\S]*\}/);

if (jsonMatch) {
  // Parse the found JSON string
  const cleanJson = jsonMatch[0];
  return { json: JSON.parse(cleanJson) };
} else {
  // Fallback if no JSON found (prevents workflow crash)
  return {
    json: {
      is_safe: false,
      summary_msg: "‚ö†Ô∏è AI Error: Could not parse analysis results.",
      detected_bad: [],
      detected_good: []
    }
  };
}

