// Get configuration and data from previous nodes
const config = $('Workflow Configuration1').first().json;
const designUpload = $('Cloudinary - Upload Design1').first().json;
const analysis = $('OpenAI Vision - Design Analysis1').first().json;

// Extract Cloudinary public_id from upload response
const designPublicId = designUpload.public_id;
const cloudName = config.cloudinaryCloudName;

// Base product image public IDs from configuration
const tshirtWhiteId = config.tshirtWhitePublicId;
const tshirtBlackId = config.tshirtBlackPublicId;
const toteBagId = config.toteBagPublicId;

// Function to generate Cloudinary transformation URL
function generateMockupUrl(baseProductId, overlayDesignId, transformations = '') {
  return `https://res.cloudinary.com/${cloudName}/image/upload/${transformations}/l_${overlayDesignId.replace(/\//g, ':')}/fl_layer_apply,g_center,w_300,h_300/${baseProductId}`;
}

// Generate mockup URLs for different products
const mockups = {
  tshirtWhite: generateMockupUrl(tshirtWhiteId, designPublicId, 'c_fill,w_800,h_800'),
  tshirtBlack: generateMockupUrl(tshirtBlackId, designPublicId, 'c_fill,w_800,h_800'),
  toteBag: generateMockupUrl(toteBagId, designPublicId, 'c_fill,w_800,h_800')
};

// Determine primary mockup based on AI analysis
let primaryMockup = mockups.tshirtWhite;
if (analysis.recommendedProducts && analysis.recommendedProducts.includes('bag')) {
  primaryMockup = mockups.toteBag;
} else if (analysis.colorPalette && analysis.colorPalette.some(color => color.toLowerCase().includes('dark') || color.toLowerCase().includes('black'))) {
  primaryMockup = mockups.tshirtBlack;
}

// Return all mockup data
return [{
  json: {
    mockups: mockups,
    primaryMockup: primaryMockup,
    designPublicId: designPublicId,
    analysis: analysis,
    cloudinaryBaseUrl: `https://res.cloudinary.com/${cloudName}/image/upload/`
  }
}];

