// Normalize input (single object or array) and return one item per attempt
const body = $json.body;
let items = [];
if (Array.isArray(body)) items = body; else if (body && Object.keys(body).length) items = [body];

return items.map(it => {
  const username = (it.username || '').toString();
  const ip = (it.ip || '').toString();
  const timestamp = it.timestamp || new Date().toISOString();
  const error = it.error || 'Login failed';
  return {
    json: {
      username,
      ip,
      timestamp,
      error,
      logMessage: `Failed login attempt detected\nUser: ${username || 'Unknown'}\nIP: ${ip || 'Unknown'}\nTime: ${timestamp}\nError: ${error}`
    }
  };
});

// Build the grouped summary message for Slack & Jira
const attempts = $json.attempts || [];
let summary = `User: ${attempts[0].username}\nIP: ${attempts[0].ip}\nTotal Attempts: ${attempts.length}\n\nAttempts:\n`;
attempts.forEach((a, idx) => {
  const time = a.timestamp || new Date(a.ts).toISOString();
  summary += `${idx + 1}. ${time} ‚Äî ${a.error}\n`;
});

return [{ json: { username: attempts[0].username, ip: attempts[0].ip, count: attempts.length, summaryMessage: summary, attempts } }];

