// Detect duplicates within a 5-minute window grouped by username+ip
const all = $input.all().map(i => i.json);
const windowMs = 5 * 60 * 1000; // 5 minutes

// Group by username|ip
const groups = {};
all.forEach(a => {
  const key = `${a.username}||${a.ip}`;
  if (!groups[key]) groups[key] = [];
  groups[key].push(a);
});

const output = [];
for (const key of Object.keys(groups)) {
  const attempts = groups[key]
    .map(a => ({ ...a, ts: new Date(a.timestamp).getTime() }))
    .sort((x, y) => x.ts - y.ts);

  // sliding window to find max attempts within windowMs
  let maxCount = 0;
  let bestSlice = [attempts[0]];
  for (let i = 0; i < attempts.length; i++) {
    let j = i;
    while (j < attempts.length && (attempts[j].ts - attempts[i].ts) <= windowMs) j++;
    const count = j - i;
    if (count > maxCount) {
      maxCount = count;
      bestSlice = attempts.slice(i, j);
    }
  }

  if (maxCount >= 2) {
    // multiple attempts within window -> grouped output
    output.push({ json: { username: attempts[0].username, ip: attempts[0].ip, multiple: true, attempts: bestSlice } });
  } else {
    // single attempt (no duplicates in window) -> output each attempt as single
    attempts.forEach(a => output.push({ json: { username: a.username, ip: a.ip, multiple: false, attempts: [a] } }));
  }
}

return output;

