// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const textBlock =
  $json.message.blocks[0].elements[0].elements[1].text || "";

const jiraUrl =
  $json.message.blocks[0].elements[0].elements[4].url || "";

function extractValue(key) {
  const regex = new RegExp(key + ":\\s*(.*)");
  const match = textBlock.match(regex);
  return match ? match[1].trim() : null;
}

return [
  {
    json: {
      username: extractValue("UserName"),
      ip: extractValue("Ip"),
      totalAttempts: Number(extractValue("TotalAttempts")),
      attemptType: extractValue("AttemptType"),
      attemptList: extractValue("Attempt List With Time & Error"),
      jira: jiraUrl,
      rawText: textBlock,
    },
  },
];
