function decodeBase64(input) {
  try {
    return Buffer.from(input, "base64").toString("utf8");
  } catch (e) {
    return "";
  }
}

// === Gmail Trigger Payload ===
const payload = $json.payload || {};
const headers = payload.headers || [];

// === BODY (multi-part fallback) ===
let bodyRaw =
  payload.body?.data ||
  payload.parts?.find(p => p.mimeType?.includes("text/plain"))?.body?.data ||
  payload.parts?.[0]?.parts?.find(p => p.mimeType?.includes("text/plain"))?.body?.data ||
  "";

let body = decodeBase64(bodyRaw).trim();

// === Snippet fallback ===
// Eƒüer body bo≈üsa Gmail trigger snippet‚Äôƒ±nƒ± kullan
if (!body || body.length < 10) {
  body = $json.snippet || "(no message content)";
}

// === FROM & SUBJECT ===
let fromHeader =
  headers.find(h => h.name?.toLowerCase() === "from")?.value ||
  $json.From ||  // root-level fallback
  "";
let subjectHeader =
  headers.find(h => h.name?.toLowerCase() === "subject")?.value ||
  $json.Subject ||  // root-level fallback
  "(no subject)";

// === Clean Email ===
let from_email = fromHeader;
const emailMatch = fromHeader.match(/<(.+?)>/);
if (emailMatch) from_email = emailMatch[1].trim();

// === Lead Name ===
const leadNameMatch = fromHeader.match(/^(.+?)\s*</);
const lead_name = leadNameMatch ? leadNameMatch[1].trim() : "Unknown Lead";

// === Output ===
return [
  {
    json: {
      lead_name,
      from_email,
      subject: subjectHeader,
      last_message: body,
      context_language: "en",
      calendar_link: "https://calendar.google.com/calendar/u/0/r",
    },
  },
];

// --- MailPilot: Personalize AI Reply (Schedule Meeting Case, Human Style) ---
return $input.all().map(item => {
  // === Input verileri ===
  const name = item.json.first_name || item.json.lead_name || "there";
  const company = item.json.company || "";
  const replyText =
    item.json.message?.content?.body ||
    item.json.reply ||
    "Thanks for getting back to me!";

  const link =
    item.json.calendar_link ||
    item.json.htmlLink ||
    item.json.link ||
    "";

  // === Header ===
  const header = "üìÖ Let's Schedule a Quick Call";

  // === HTML Body ===
  const html = `
  <div style="font-family: Arial, sans-serif; font-size:15px; color:#202124; line-height:1.6; max-width:600px; margin:0 auto;">
    <h2 style="color:#1a73e8; font-size:18px; margin-bottom:16px;">${header}</h2>

    <p>Hi ${name},</p>

    <p>${replyText}</p>

    <p>You can book a quick call using my calendar link below:</p>
    <p><a href="${link}" target="_blank" style="color:#1a73e8;">${link}</a></p>

    <p>Looking forward to connecting soon!</p>

    <p>Best regards,<br>
    <strong>Your Name</strong><br>
    <small>Your Company</small><br>
    <a href="mailto:yourmail" style="color:#1a73e8;">yourmail</a></p>
  </div>
  `;

  item.json.subject =
    item.json.subject || "Schedule a Quick Call ‚Äî MailPilot AI Reply";
  item.json.finalMessage = html;
  return item;
});

// --- MailPilot: Personalize Greeting (Clean white, no background) ---
return $input.all().map(item => {
  // === 1) Input Deƒüerleri ===
  const name = item.json.first_name || item.json.lead_name || "there";
  const subject = item.json.subject || "Following up";
  const content = item.json.message?.content?.body || item.json.body || "";
  const senderName = "Your Name";
  const senderEmail = "Your Mail";

  // === 2) Ba≈ülƒ±k (Friendly follow-up tone) ===
  const header = "üí¨ Just Checking In ‚Äî Hope You're Doing Well";

  // === 3) HTML Body (Outbound tarzƒ± sade) ===
  const htmlBody = `
  <div style="font-family: Arial, sans-serif; font-size:15px; color:#202124; line-height:1.6; max-width:600px; margin:0 auto;">
    <h2 style="color:#1a73e8; font-size:18px; margin-bottom:16px;">${header}</h2>

    <p>Hi ${name},</p>

    <p>I just wanted to quickly follow up on our previous conversation.</p>
    <p>${content || "I‚Äôd love to reconnect and see how things are going on your end."}</p>

    <p>Best regards,<br>
    <strong>${senderName}</strong><br>
    <small>Your Company</small><br>
    <a href="mailto:${senderEmail}" style="color:#1a73e8;">${senderEmail}</a></p>
  </div>
  `;

  // === 4) Output ===
  item.json.subject = subject;
  item.json.finalMessage = htmlBody;
  return item;
});

