const items = $input.all().map(i => i.json);

const anomalies = items;
const totalIssues = anomalies.length;
const totalRows = anomalies.length; 

const score = totalIssues === 0
  ? 100
  : Math.max(0, 100 - Math.round((totalIssues / (totalRows || 1)) * 100));

let summary = `üìä Data Quality Report\n\n`;
summary += `Anomalies found: ${totalIssues}\n`;
summary += `Quality score: ${score}/100\n\n`;

anomalies.slice(0, 5).forEach((a, i) => {
  summary += `${i + 1}. id=${a.id ?? "N/A"} | sku=${a.sku ?? "N/A"} | price=${a.price ?? "N/A"}\n`;
});


return [
  {
    json: {
      anomalies_found: totalIssues,
      totalIssues,
      totalRows,
      qualityScore: score,
      summary,          
      summary_text: summary,
      anomalies
    }
  }
];

