const rows = items[0].json.data;

const result = {acf: {}};

for (const row of rows) {
  const lang = row.translated_language;

  result.acf[`content_${lang}`] = row.translated_news.message;
  result.acf[`title_${lang}`]   = row.translated_news.title;
}

return [{ json: result }];

