// Separate response data and pagination information
const response = $input.first().json;

// Prepare data array for downstream processing (for example, Google Sheets)
const ads = response.data || [];

// Check if there is a link to the next page
const nextUrl = response.paging && response.paging.next ? response.paging.next : null;

// Return a normalized object with data and next_url
return {
  json: {
    data: ads,
    next_url: nextUrl
  }
};

const ids = items
  .map(item => item.json.id)
  // Cast all values to strings to avoid mismatches like "123" vs 123
  .map(id => id != null ? String(id) : undefined)
  .filter((id, index, arr) => id && arr.indexOf(id) === index);

return [{ json: { existingIds: ids } }];

const ads = $json.data || [];
// existingIds may be numbers or strings ‚Äî normalize everything to strings
const existingIds = ($json.existingIds || []).map(id => id != null ? String(id) : undefined).filter(Boolean);
const seen = new Set(existingIds);
const newAds = [];

for (const ad of ads) {
  if (!ad?.id) continue;
  const adId = String(ad.id);
  if (!seen.has(adId)) {
    newAds.push(ad);
    seen.add(adId); // protect against duplicates within a single run and across runs
  }
}

return {
  json: {
    data: newAds
  }
};
