// Get current data
const productUrl = $('Get Products').first().json.URL_Product;
const currentPrice = parseFloat($('Extract Price').first().json.price.replace(/[$,]/g, ''));
const today = $now.toFormat('yyyy-MM-dd');
const timestamp = $now.toISO();

// Get existing history for this product today
const historyItems = $('Get Price History').all();

// Check if there's already a record for this product today
const existingTodayRecord = historyItems.find(item => 
  item.json.URL_Product === productUrl && 
  item.json.Date === today
);

// Prepare the data to return (will be used by next node to update sheet)
return {
  url_product: productUrl,
  price: currentPrice,
  date: today,
  timestamp: timestamp,
  should_update: !!existingTodayRecord,
  row_number: existingTodayRecord ? existingTodayRecord.json.row_number : null
};

// Get current price
const currentPriceText = $('Extract Price').first().json.price;
const currentPrice = parseFloat(currentPriceText.replace(/[$,]/g, ''));

// Get price history (last 60 days)
const historyItems = $('Get Price History').all();

// Sort by date descending and get last 60 entries
const priceHistory = historyItems
  .map(item => ({
    date: item.json.Date,
    price: parseFloat(item.json.Price)
  }))
  .sort((a, b) => new Date(b.date) - new Date(a.date))
  .slice(0, 60);

// Calculate statistics
const prices = priceHistory.map(h => h.price);
const avgPrice = prices.length > 0 ? prices.reduce((a, b) => a + b, 0) / prices.length : currentPrice;
const minPrice = prices.length > 0 ? Math.min(...prices) : currentPrice;
const maxPrice = prices.length > 0 ? Math.max(...prices) : currentPrice;

// Prepare data for AI
const last30Days = priceHistory.slice(0, 30);
const priceData = last30Days.map(h => `${h.date}: $${h.price.toFixed(2)}`).join('\n');

const targetPrice = $('Get Products').first().json.Target_Price;

return {
  current_price: currentPrice,
  target_price: targetPrice,
  avg_price_60d: avgPrice.toFixed(2),
  min_price_60d: minPrice.toFixed(2),
  max_price_60d: maxPrice.toFixed(2),
  history_length: priceHistory.length,
  price_data_text: priceData,
  full_history: priceHistory
};

