// Parse AI response
let aiResponse;
try {
  const content =  $input.first().json.output || $input.first().json.text || '';
  // Try to extract JSON if wrapped in markdown
  const jsonMatch = content.match(/```json\s*([\s\S]*?)```/) || content.match(/({[\s\S]*})/);
  const jsonStr = jsonMatch ? jsonMatch[1] : content;
  aiResponse = JSON.parse(jsonStr.trim());
} catch (error) {
  // Fallback if parsing fails
  aiResponse = {
    recommendation: 'WAIT',
    confidence: 50,
    reason: 'Unable to parse AI response',
    urgency_score: 50,
    predicted_trend: 'STABLE'
  };
}

// Get data from Loop context
const loopItem = $('Loop Products').first().json;
const productUrl = loopItem.URL_Product;
const productName = loopItem.Product_Name || 'Product';
const userEmail = loopItem.User_Email;
const targetPrice = loopItem.Target_Price;

// Get analysis data
const analysisData = $('Prepare AI Data').item.json;
const currentPrice = analysisData.current_price;
const avgPrice = analysisData.avg_price_60d;
const minPrice = analysisData.min_price_60d;
const maxPrice = analysisData.max_price_60d;
const priceHistory = analysisData.full_history;

// Determine if should buy
const shouldBuy = aiResponse.recommendation === 'BUY' && currentPrice <= targetPrice;

// Prepare chart data
const chartData = priceHistory.slice(0, 30).reverse();
const chartLabels = chartData.map(h => h.date);
const chartPrices = chartData.map(h => h.price);

return {
  product_url: productUrl,
  product_name: productName,
  user_email: userEmail,
  current_price: currentPrice,
  target_price: targetPrice,
  avg_price: avgPrice,
  min_price: minPrice,
  max_price: maxPrice,
  ai_recommendation: aiResponse.recommendation,
  ai_confidence: aiResponse.confidence,
  ai_reason: aiResponse.reason,
  urgency_score: aiResponse.urgency_score,
  predicted_trend: aiResponse.predicted_trend,
  should_buy: shouldBuy,
  chart_labels: chartLabels,
  chart_prices: chartPrices
};

// Generate QuickChart.io URL for price history chart
const labels = $json.chart_labels;
const prices = $json.chart_prices;
const currentPrice = $json.current_price;
const targetPrice = $json.target_price;

const chartConfig = {
  type: 'line',
  data: {
    labels: labels,
    datasets: [
      {
        label: 'Price History',
        data: prices,
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        tension: 0.1,
        fill: true
      },
      {
        label: 'Target Price',
        data: Array(labels.length).fill(targetPrice),
        borderColor: 'rgb(255, 99, 132)',
        borderDash: [5, 5],
        fill: false,
        pointRadius: 0
      }
    ]
  },
  options: {
    title: {
      display: true,
      text: 'Price History (Last 30 Days)'
    },
    scales: {
      yAxes: [{
        ticks: {
          callback: function(value) {
            return '$' + value.toFixed(2);
          }
        }
      }]
    }
  }
};

const encodedConfig = encodeURIComponent(JSON.stringify(chartConfig));
const chartUrl = `https://quickchart.io/chart?c=${encodedConfig}&width=600&height=300`;

return {
  ...($json),
  chart_url: chartUrl
};
