(() => {
  const emp = $('Set Timesheet JSON Fields').item.json['Employee Name']?.replace(/\s+/g,'') ?? 'Unknown';

  // Clean up and normalise date string
  let raw = $('Set Timesheet JSON Fields').item.json['Week Start Date']?.trim();
  raw = raw.replace(/\//g, '-'); // 08/25/2025 -> 08-25-2025

  const [mm, dd, yyyy] = raw.split('-');
  const startDate = new Date(`${yyyy}-${mm}-${dd}T12:00:00`);
  const endDate = new Date(startDate.getTime() + 13 * 86400000);

  const pad = n => n.toString().padStart(2, '0');
  const format = d => `${pad(d.getMonth() + 1)}-${pad(d.getDate())}-${d.getFullYear()}`;

  return `${emp}_${format(startDate)}_to_${format(endDate)}`;
})()
}}

={{
(() => {
  // clean employee name
  const emp = $('Set Timesheet JSON Fields').item.json['Employee Name']?.replace(/\s+/g,'') ?? 'Unknown';

  // get end date string and normalise it
  let rawEnd = $('Set Timesheet JSON Fields').item.json['Week End Date']?.trim() || '';
  rawEnd = rawEnd.replace(/\//g,'-');

  // split into parts
  const [mmE, ddE, yyyyE] = rawEnd.split('-');

  // build Date object for end date
  const endDate = new Date(`${yyyyE}-${mmE}-${ddE}T12:00:00`);

  // subtract 13 days to get start date
  const startDate = new Date(endDate.getTime() - 13 * 86400000);

  // helper to pad numbers
  const pad = n => n.toString().padStart(2,'0');
  const format = d => `${pad(d.getMonth()+1)}-${pad(d.getDate())}-${d.getFullYear()}`;

  return `${emp}_${format(startDate)}_to_${format(endDate)}`;
})()
}}
