// Get input items
const items = $input.all();

// Config - Updated to start from current date
const RAMP_START = new Date('2025-09-21'); // Changed from '2025-09-28'
const LIMIT_BY_WEEK = [150];  // weeks 1..4+, capped at last

// Week since start (1-based, clamped)
const msPerWeek = 7 * 24 * 60 * 60 * 1000;
const weeksSinceStart = Math.floor((Date.now() - RAMP_START.getTime()) / msPerWeek) + 1;
const dailyLimit = LIMIT_BY_WEEK[Math.min(weeksSinceStart, LIMIT_BY_WEEK.length) - 1];

// Local "today" to avoid UTC boundary surprises (00:00 local)
const now = new Date();
const today = `${now.getFullYear()}-${String(now.getMonth()+1).padStart(2,'0')}-${String(now.getDate()).padStart(2,'0')}`;

// Get/reset counter
let sentToday = $getWorkflowStaticData('node');
if (!sentToday.date || sentToday.date !== today) {
  sentToday.date = today;
  sentToday.count = 0;
}

// Calculate how many emails we can still send today
const remainingToday = Math.max(0, dailyLimit - sentToday.count);

console.log('Rate limiting info:', { 
  today, 
  currentCount: sentToday.count, 
  dailyLimit, 
  remainingToday, 
  weeksSinceStart,
  totalInputEmails: items.length
});

// If no emails can be sent today, return empty array
if (remainingToday === 0) {
  console.log('Daily limit reached - no emails will be sent');
  return [];
}

// Take only the emails we can send today
const emailsToSend = items.slice(0, remainingToday);

// DON'T UPDATE COUNTER HERE - wait until emails are actually sent
console.log(`Preparing to send ${emailsToSend.length} emails. Current count: ${sentToday.count}/${dailyLimit}`);

// Return ALL emails to send with metadata
return emailsToSend.map((item, index) => ({
  json: { 
    ...item.json, 
    canSend: true, 
    currentSentToday: sentToday.count,
    dailyLimit, 
    weeksSinceStart,
    batchSize: emailsToSend.length,
    emailIndex: index + 1,
    totalToSend: emailsToSend.length
  }
}));

// Update the counter for successfully sent email
const item = $input.first();

// Get today's date
const now = new Date();
const today = `${now.getFullYear()}-${String(now.getMonth()+1).padStart(2,'0')}-${String(now.getDate()).padStart(2,'0')}`;

// Get/update counter
let sentToday = $getWorkflowStaticData('node');
if (!sentToday.date || sentToday.date !== today) {
  sentToday.date = today;
  sentToday.count = 0;
}

// Increment counter by 1 for this successfully sent email
sentToday.count += 1;

console.log(`Email sent successfully to: ${item.json.Email}`);
console.log(`Total sent today: ${sentToday.count}`);

// Return the item with success status
return [{
  json: {
    ...item.json,
    emailStatus: 'sent',
    totalSentToday: sentToday.count,
    sentDate: new Date().toISOString(),
    success: true
  }
}];

// Handle failed email
const item = $input.first();

// Extract error message if available
let errorMessage = 'Unknown error';
if (item.json.error) {
  errorMessage = typeof item.json.error === 'string' ? item.json.error : JSON.stringify(item.json.error);
} else if (item.json.message) {
  errorMessage = item.json.message;
}

console.log(`Email failed to send to: ${item.json.Email}`);
console.log(`Error: ${errorMessage}`);

// Return item with failed status
return [{
  json: {
    ...item.json,
    emailStatus: 'failed',
    failureReason: errorMessage,
    failedDate: new Date().toISOString(),
    success: false
  }
}];
