// n8n Code node - Transform transaction data

const data = $input.item.json;

const output = [];

if (data.output && data.output.Transaction && Array.isArray(data.output.Transaction)) {
  data.output.Transaction.forEach(transaction => {
    output.push({
      "Type": transaction.Type || "",
      "Date": transaction.Date || "",
      "Payee": transaction.Payee || "",
      "Amount": transaction.Amount || "",
      "Account": transaction.Account || "",
      "Description": transaction.Description || "",
      "Category": transaction.Category || ""
    });
  });
}

return output;

// Get the input data - handle different possible structures
const items = $input.all();
let inputData;

// Try to find the data in different possible locations
if (items[0].json[0]) {
  inputData = items[0].json[0];
} else if (items[0].json) {
  inputData = items[0].json;
} else if (Array.isArray(items[0])) {
  inputData = items[0][0];
} else {
  inputData = items[0];
}

// Check if we have the expected structure
if (!inputData.included || !inputData.data) {
  // Return the structure so you can see what you're receiving
  return [{
    error: "Data structure not as expected",
    receivedKeys: Object.keys(inputData),
    sampleData: inputData
  }];
}

// Extract budget data with spending info
const budgets = inputData.included;
const budgetLimits = inputData.data;

// Create a map of budget spending from limits data
const spendingMap = {};
budgetLimits.forEach(limit => {
  const budgetId = limit.attributes.budget_id;
  const spent = limit.attributes.spent;
  const spentAmount = spent.length > 0 ? parseFloat(spent[0].sum) : 0;
  const pcAmount = parseFloat(limit.attributes.pc_amount);
  const pcSpent = limit.attributes.pc_spent.length > 0 ? Math.abs(parseFloat(limit.attributes.pc_spent[0].sum)) : 0;

  spendingMap[budgetId] = {
    spent: Math.abs(spentAmount),
    pcAmount: pcAmount,
    pcSpent: pcSpent
  };
});

// Transform into structured array
const parsedData = budgets.map(budget => {
  const attrs = budget.attributes;
  const budgetId = budget.id;
  const pcAmount = parseFloat(attrs.pc_auto_budget_amount);
  const pcSpent = spendingMap[budgetId]?.pcSpent || 0;
