  return {
    budgetCategory: attrs.name,
    percentUsed: parseFloat(percentUsed),
    pcAmount: parseFloat(pcAmount.toFixed(2)),
    pcSpent: parseFloat(pcSpent.toFixed(2)),
    pcRemaining: parseFloat(pcRemaining.toFixed(2)),
    order: attrs.order,
    pcCurrencyCode: attrs.primary_currency_code,
    pcCurrencySymbol: attrs.primary_currency_symbol
  };
});

// Sort by budget order
parsedData.sort((a, b) => a.order - b.order);

// Calculate totals
const totals = parsedData.reduce((acc, item) => {
  acc.pcAmount += item.pcAmount;
  acc.pcSpent += item.pcSpent;
  acc.pcRemaining += item.pcRemaining;
  return acc;
}, { amount: 0, spent: 0, remaining: 0, pcAmount: 0, pcSpent: 0, pcRemaining: 0 });

// Calculate overall percentage
const totalPercentUsed = totals.amount > 0 ? ((totals.spent / totals.amount) * 100).toFixed(1) : 0;

// Add totals row
parsedData.push({
  budgetCategory: 'TOTAL',
  percentUsed: parseFloat(totalPercentUsed),
  pcAmount: parseFloat(totals.pcAmount.toFixed(2)),
  pcSpent: parseFloat(totals.pcSpent.toFixed(2)),
  pcRemaining: parseFloat(totals.pcRemaining.toFixed(2)),
  order: 999,
});

return parsedData;
