// Define your target developer searches
const searches = [
  {
    location: "New Jersey",
    language: "javascript",
    minFollowers: 50
  },
  {
    location: "Texas",
    language: "python",
    minFollowers: 100
  },
  {
    location: "New York",
    language: "react",
    minFollowers: 200
  }
];

return searches.map(search => ({ json: search }));

const allInputs = $input.all();   // get all input items
const developers = [];

allInputs.forEach(input => {
  const users = input.json.items || [];

  users.forEach(user => {
    developers.push({
      json: {
        username: user.login,
        profileUrl: user.html_url,
        avatarUrl: user.avatar_url,
        userApiUrl: user.url
      }
    });
  });
});

return developers;

const data = $input.item.json;

// Extract website/blog URL and domain
let website = data.blog || "";
let domain = "";

if (website) {
  try {
    // Clean up the URL
    if (!website.startsWith('http')) {
      website = 'https://' + website;
    }
    const url = new URL(website);
    domain = url.hostname.replace('www.', '');
  } catch (e) {
    domain = website;
  }
}

// Calculate lead score based on GitHub activity
let score = 20; // Base score

// Followers (max 30 points)
const followers = data.followers || 0;
if (followers > 500) score += 30;
else if (followers > 200) score += 25;
else if (followers > 100) score += 20;
else if (followers > 50) score += 15;
else score += 10;

// Public repos (max 20 points)
const repos = data.public_repos || 0;
if (repos > 50) score += 20;
else if (repos > 20) score += 15;
else if (repos > 10) score += 10;
else score += 5;

// Has contact info (max 30 points)
if (data.email) score += 15;
if (website && website !== "") score += 10;
if (data.company) score += 5;

// Determine quality
let quality = "Cold";
if (score >= 70) quality = "Hot";
else if (score >= 50) quality = "Warm";

const today = new Date().toISOString().split('T')[0];

return {
  json: {
    name: data.name || data.login,
    username: data.login,
    email: data.email || "Check website",
    bio: data.bio || "",
    company: data.company || "",
    location: data.location || "",
    website: website,
    domain: domain,
    twitter: data.twitter_username ? `https://twitter.com/${data.twitter_username}` : "",
    followers: followers,
    following: data.following || 0,
    publicRepos: repos,
    profileUrl: data.html_url,
    avatarUrl: data.avatar_url,
    leadSource: "GitHub",
    leadScore: Math.min(score, 100),
    leadQuality: quality,
    scrapedDate: today,
    notes: `GitHub developer with ${repos} repos and ${followers} followers. ${data.bio || ''}`
  }
};

const hunterData = $input.item.json.data || {};
const emails = hunterData.emails || [];
const previousData = $('Extract Developer Data').item.json;

let bestEmail = previousData.email;

if (emails.length > 0) {
  const sortedEmails = emails.sort((a, b) => b.confidence - a.confidence);
  bestEmail = sortedEmails[0].value;
}

return {
  json: {
    ...previousData,
    email: bestEmail,
    emailConfidence: emails[0]?.confidence || 0
  }
};

const existingLeads = $('Check For Duplicates').all();
const currentLead = $input.item.json;

// Check if username already exists
const isDuplicate = existingLeads.some(item => {
  const row = item.json;
  return row['Business Name'] === currentLead.name || 
         row.Email === currentLead.email ||
         (row.Notes && row.Notes.includes(currentLead.username));
});

if (isDuplicate) {
  return [];
}

return [{ json: currentLead }];

const data = $input.item.json;
const timestamp = Date.now();
const randomNum = Math.floor(Math.random() * 1000);
const leadId = `GITHUB-${timestamp}-${randomNum}`;

return {
  json: {
    leadId: leadId,
    businessName: data.name,
    contactName: data.name,
    jobTitle: "Developer",
    email: data.email,
    phone: "Not available",
    website: data.website,
    address: data.location,
    industry: "Software Development",
    companySize: data.company || "Freelancer",
    rating: 0,
    reviews: 0,
    leadScore: data.leadScore,
    leadQuality: data.leadQuality,
    scrapedDate: data.scrapedDate,
    status: "New - Scraped",
    contacted: "No",
    notes: data.notes,
    leadSource: data.leadSource
  }
};
