/**
 * PRE-EXTRACT SEO (HTML -> compact payload)
 * Reads:  $json.results[0].content
 * Returns: seo_compact_text + seo_fields
 */

function decodeHtml(s) {
  if (!s || typeof s !== "string") return s;
  return s
    .replace(/&amp;/g, "&")
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">")
    .replace(/&gt;/g, ">")
    .replace(/&#(\d+);/g, (_, code) => String.fromCharCode(Number(code)));
}

function cleanSpaces(s) {
  return (s || "")
    .replace(/\s+/g, " ")
    .trim();
}

function stripTags(html) {
  return cleanSpaces(
    (html || "")
      .replace(/<script[\s\S]*?<\/script>/gi, " ")
      .replace(/<style[\s\S]*?<\/style>/gi, " ")
      .replace(/<!--[\s\S]*?-->/g, " ")
      .replace(/<[^>]*>/g, " ")
  );
}

function getFirstMatch(html, regex) {
  const m = html.match(regex);
  return m ? decodeHtml(m[1]).trim() : null;
}

function getAllMatches(html, regex, limit = 20) {
  const out = [];
  let m;
  while ((m = regex.exec(html)) !== null) {
    out.push(decodeHtml(m[1]).trim());
    if (out.length >= limit) break;
  }
  return out;
}

function truncate(s, maxChars) {
  if (!s) return "";
  return s.length > maxChars ? s.slice(0, maxChars) + "‚Ä¶" : s;
}

const html = $json?.results?.[0]?.content;
if (!html || typeof html !== "string") {
  return [{
    json: {
      seo_compact_text: "NO_HTML_INPUT",
      seo_fields: { title: null, meta_description: null, h1: null, h2: [], canonical: null, og_title: null, og_description: null, text_excerpt: "" }
    }
  }];
}

// Basic head elements
const title = getFirstMatch(html, /<title[^>]*>([\s\S]*?)<\/title>/i);
const meta_description =
  getFirstMatch(html, /<meta[^>]*name=["']description["'][^>]*content=["']([^"']*)["']/i) ||
  getFirstMatch(html, /<meta[^>]*content=["']([^"']*)["'][^>]*name=["']description["']/i);

const canonical =
  getFirstMatch(html, /<link[^>]*rel=["']canonical["'][^>]*href=["']([^"']+)["']/i) ||
  getFirstMatch(html, /<link[^>]*href=["']([^"']+)["'][^>]*rel=["']canonical["']/i);

// OG tags
const og_title =
  getFirstMatch(html, /<meta[^>]*property=["']og:title["'][^>]*content=["']([^"']*)["']/i) ||
  getFirstMatch(html, /<meta[^>]*content=["']([^"']*)["'][^>]*property=["']og:title["']/i);

const og_description =
  getFirstMatch(html, /<meta[^>]*property=["']og:description["'][^>]*content=["']([^"']*)["']/i) ||
  getFirstMatch(html, /<meta[^>]*content=["']([^"']*)["'][^>]*property=["']og:description["']/i);

// H1/H2 (simple regex extraction)
const h1 = getFirstMatch(html, /<h1[^>]*>([\s\S]*?)<\/h1>/i);
const h2 = getAllMatches(html, /<h2[^>]*>([\s\S]*?)<\/h2>/gi, 15).map(x => cleanSpaces(stripTags(x))).filter(Boolean);

// Visible text excerpt (limited)
const visibleText = stripTags(html);
const text_excerpt = truncate(visibleText, 6000);

// Build compact text for LLM
const lines = [];
lines.push("SEO_PAGE_EXTRACT");
lines.push("---");
lines.push(`TITLE: ${title || "null"}`);
lines.push(`META_DESCRIPTION: ${meta_description || "null"}`);
lines.push(`CANONICAL: ${canonical || "null"}`);
lines.push(`H1: ${h1 ? cleanSpaces(stripTags(h1)) : "null"}`);
lines.push(`H2: ${h2.length ? h2.join(" | ") : "null"}`);
lines.push(`OG_TITLE: ${og_title || "null"}`);
lines.push(`OG_DESCRIPTION: ${og_description || "null"}`);
lines.push("---");
lines.push("VISIBLE_TEXT_EXCERPT:");
lines.push(text_excerpt || "");

return [{
  json: {
    seo_compact_text: lines.join("\n"),
    seo_fields: {
      title,
      meta_description,
      canonical,
      h1: h1 ? cleanSpaces(stripTags(h1)) : null,
      h2,
      og_title,
      og_description,
      text_excerpt
    }
  }
}];

// Input esperado: items[0].json.output (string con ```json ... ```)
// Output: 1 item con el JSON ya parseado en items[0].json

function stripCodeFences(s) {
  if (!s || typeof s !== 'string') return s;
  // Quita ```json ... ``` o ``` ... ```
  return s.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/i, '').trim();
}

function extractFirstJSONObject(s) {
  // Extrae el primer bloque {...} v√°lido
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  if (start === -1 || end === -1 || end <= start) throw new Error('No JSON object braces found');
  return s.slice(start, end + 1);
}

const out = [];
for (const item of items) {
  let raw = item.json.output ?? item.json ?? '';
  if (typeof raw !== 'string') raw = JSON.stringify(raw);

  // Si viene envuelto en array tipo [ { output: "..." } ], deshazlo
  // (por si te llega el array entero aqu√≠)
  if (Array.isArray(item.json) && item.json.length && item.json[0].output) {
    raw = item.json[0].output;
  }

  raw = stripCodeFences(raw);
  const jsonStr = extractFirstJSONObject(raw);

  let parsed;
  try {
    parsed = JSON.parse(jsonStr);
  } catch (e) {
    // Intento de ‚Äúrepair‚Äù sencillo: elimina BOM y comas colgantes comunes
    const repaired = jsonStr
      .replace(/^\uFEFF/, '')
      .replace(/,\s*}/g, '}')
      .replace(/,\s*]/g, ']');
    parsed = JSON.parse(repaired);
  }

  out.push({ json: parsed });
}
return out;
