// üßπ FILENAME PARSER
// Naming convention: "user@example.com"

// 1. Get filename
const inputName = $input.first().json.name;
const filename = inputName ? inputName.replace(/\.pdf$/i, '') : 'user@example.com';

// 2. Split by underscore
const parts = filename.split('_');

// 3. Assign variables
const clientName = parts[0] || 'UnknownClient';
const projectName = parts[1] || 'NewProject';
const contactEmail = parts[2] || 'admin@example.com';
const startDate = new Date().toISOString().split('T')[0];

// 4. Generate Slack channel name (alphanumeric + hyphens only)
let safeName = clientName.toLowerCase()
    .replace(/\s+/g, '-')
    .replace(/[^a-z0-9\-]/g, '')
    .replace(/-+/g, '-')
    .replace(/^-|-$/g, '');

// Fallback for non-ASCII names
if (!safeName || safeName.length < 2) {
    const randomId = Math.floor(1000 + Math.random() * 9000);
    safeName = `contract-${startDate.replace(/-/g, '')}-${randomId}`;
}

const slackChannelName = `proj-${safeName}`;
const projectCode = (clientName.substring(0, 3) || 'PRJ').toUpperCase() + '-' + Date.now().toString().slice(-4);

return {
  json: {
    'Client Name': clientName,
    'Project Name': projectName,
    'Contact Email': contactEmail,
    'Start Date': startDate,
    slackChannelName,
    projectCode,
    originalFileUrl: $input.first().json.webViewLink || ''
  }
};

