// Initialize pagination variables
// Change groupId to target different LinkedIn groups
return [
  {
    json: {
      groupId: "9357376",  // Product Hunt Promotion Group
      count: 50,           // Members per request (max 50)
      start: 0,            // Starting offset
      hasMore: true,       // Continue flag
      allMembers: []       // Accumulated results
    }
  }
];

// Get current pagination state from the previous Code node's output
const items = $input.all();
const firstItem = items[0].json;

// Access the API response data (merged with pagination state)
const response = firstItem;
const members = response.members || [];
const hasMore = response.hasMore || false;

// Get pagination info from workflow context
const currentStart = $('Initialize Pagination').first().json.start || 0;
const currentCount = $('Initialize Pagination').first().json.count || 50;
const groupId = $('Initialize Pagination').first().json.groupId;
const previousMembers = $('Initialize Pagination').first().json.allMembers || [];

// Filter for premium or verified members
const filteredMembers = members.filter(member => {
  const isPremium = member.isPremium === true;
  const isVerified = member.isVerified === true;
  const hasPremiumBadge = member.badges && member.badges.includes('premium');
  const hasVerifiedBadge = member.badges && member.badges.includes('verified');

  return isPremium || isVerified || hasPremiumBadge || hasVerifiedBadge;
});

// Extract relevant fields from filtered members
const processedMembers = filteredMembers.map(member => ({
  profileId: member.profileId,
  firstName: member.firstName,
  lastName: member.lastName,
  fullName: member.fullName,
  headline: member.headline,
  publicIdentifier: member.publicIdentifier,
  profileUrl: member.profileUrl,
  followerCount: member.followerCount,
  isPremium: member.isPremium,
  isVerified: member.isVerified,
  badges: member.badges ? member.badges.join(', ') : '',
  relationshipStatus: member.relationshipStatus,
  creator: member.creator,
  fetchedAt: new Date().toISOString()
}));

// Combine with previous members
const allMembers = [...previousMembers, ...processedMembers];

// Prepare next iteration or final output
if (hasMore) {
  return [{
    json: {
      groupId: groupId,
      count: currentCount,
      start: currentStart + currentCount,
      hasMore: true,
      allMembers: allMembers,
      continueLoop: true,
      currentBatch: processedMembers
    }
  }];
} else {
  return [{
    json: {
      groupId: groupId,
      hasMore: false,
      allMembers: allMembers,
      continueLoop: false,
      totalFiltered: allMembers.length,
      totalFetched: currentStart + members.length
    }
  }];
}

// Loop back with updated pagination state
const item = $input.first().json;

return [{
  json: {
    groupId: item.groupId,
    count: item.count,
    start: item.start,
    hasMore: item.hasMore,
    allMembers: item.allMembers
  }
}];

// Split all members into individual items for Google Sheets
const allMembers = $input.first().json.allMembers || [];

if (allMembers.length === 0) {
  return [];
}

// Return each member as a separate item
return allMembers.map(member => ({ json: member }));
