// Parse Yelp business page HTML to extract structured data
const html = $input.item.json.Content || '';
const url = $input.item.json.URL || '';

// Helper to decode HTML entities
function decodeHTML(str) {
  if (!str) return '';
  return str
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&#x27;/g, "'")
    .replace(/&#x2F;/g, '/');
}

// Extract business name
let name = '';
const titleMatch = html.match(/<title[^>]*>([^<]+)/i);
if (titleMatch) {
  name = titleMatch[1].split(' - ')[0].split(' | ')[0].trim();
}
const jsonLdMatch = html.match(/"name"\s*:\s*"([^"]+)"/i);
if (jsonLdMatch) {
  name = decodeHTML(jsonLdMatch[1]);
}

// Extract overall rating
let overall_rating = '';
const ratingMatch = html.match(/"aggregateRating"[^}]*"ratingValue"\s*:\s*"?([\d.]+)"?/i);
if (ratingMatch) {
  overall_rating = ratingMatch[1];
} else {
  const altRating = html.match(/aria-label="([\d.]+)\s*star\s*rating"/i);
  if (altRating) overall_rating = altRating[1];
}

// Extract review count
let reviews_count = '';
const reviewMatch = html.match(/"reviewCount"\s*:\s*"?(\d+)"?/i);
if (reviewMatch) {
  reviews_count = reviewMatch[1];
} else {
  const altReview = html.match(/(\d+)\s*reviews?/i);
  if (altReview) reviews_count = altReview[1];
}

// Extract phone number
let phone = '';
const phoneMatch = html.match(/"telephone"\s*:\s*"([^"]+)"/i);
if (phoneMatch) {
  phone = phoneMatch[1];
} else {
  const altPhone = html.match(/\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}/);
  if (altPhone) phone = altPhone[0];
}

// Extract address
let address = '';
const addressMatch = html.match(/"address"\s*:\s*\{[^}]*"streetAddress"\s*:\s*"([^"]+)"/i);
if (addressMatch) {
  address = decodeHTML(addressMatch[1]);
}
const cityMatch = html.match(/"addressLocality"\s*:\s*"([^"]+)"/i);
const stateMatch = html.match(/"addressRegion"\s*:\s*"([^"]+)"/i);
const zipMatch = html.match(/"postalCode"\s*:\s*"([^"]+)"/i);
if (cityMatch) address += `, ${cityMatch[1]}`;
if (stateMatch) address += `, ${stateMatch[1]}`;
if (zipMatch) address += ` ${zipMatch[1]}`;

// Extract price range
let price_range = '';
const priceMatch = html.match(/"priceRange"\s*:\s*"([^"]+)"/i);
if (priceMatch) {
  price_range = priceMatch[1];
} else {
  const dollarMatch = html.match(/>\s*(\$+)\s*</i);
  if (dollarMatch) price_range = dollarMatch[1];
}

// Extract categories
let categories = '';
const categoryMatches = html.match(/"itemListElement"[^\]]*\]/i);
if (categoryMatches) {
  const cats = categoryMatches[0].match(/"name"\s*:\s*"([^"]+)"/gi);
  if (cats) {
    categories = cats.map(c => c.match(/"([^"]+)"$/)[1]).join(', ');
  }
}

// Extract images (first 5)
let images_videos_urls = '';
const imageMatches = html.match(/https:\/\/s3-media[\d]*\.fl\.yelpcdn\.com\/bphoto\/[^"'\s]+/gi);
if (imageMatches) {
  const uniqueImages = [...new Set(imageMatches)].slice(0, 5);
  images_videos_urls = uniqueImages.join(', ');
}

// Extract website
let website = '';
const websiteMatch = html.match(/biz-website[^>]*>\s*<a[^>]*href="([^"]+)"/i);
if (websiteMatch) {
  website = decodeHTML(websiteMatch[1]);
}

// Extract business hours
let hours = '';
const hoursMatch = html.match(/"openingHours"\s*:\s*\[([^\]]+)\]/i);
if (hoursMatch) {
  hours = hoursMatch[1].replace(/"/g, '').replace(/,/g, '; ');
}

return {
  json: {
    name: name || 'N/A',
    overall_rating: overall_rating || 'N/A',
    reviews_count: reviews_count || 'N/A',
    url: url,
    phone: phone || 'N/A',
    address: address || 'N/A',
    price_range: price_range || 'N/A',
    categories: categories || 'N/A',
    website: website || 'N/A',
    hours: hours || 'N/A',
    images_videos_urls: images_videos_urls || 'N/A',
    scraped_at: new Date().toISOString()
  }
};
