const notion = $items("Fetch Records from Notion Database").map(i => i.json.properties);
const airtable = $items("Fetch Records from Airtable").map(i => i.json);

return [
  { json: { notion, airtable } }
];

// Input contains:
// $json.notion = Notion item
// $json.airtable = array of Airtable records

const notionItem = $json.notion[0];   // Just 1 item
const airtableItems = $json.airtable; // Multiple items in array

// Extract the Notion fields
const notionName = notionItem["Item Name"].title[0].plain_text;
const notionCount = notionItem["Quantity in Stock"].number;

// Find matching Airtable item by item name
const match = airtableItems.find(item => item["Item Name"] === notionName);

if (!match) {
  return [{
    json: {
      status: "no_match_found",
      item: notionName
    }
  }];
}

const airtableCount = match["Quantity in Stock"];

// Compare both
const diff = notionCount - airtableCount;

return [{
  json: {
    status: "match_found",
    item: notionName,
    notionCount,
    airtableCount,
    difference: diff,
    airtableRecordId: match.id,
    needsUpdate: diff !== 0
  }
}];
