function htmlToText(html) {
  if (!html || typeof html !== "string") return "";

  let text = html;

  text = text.replace(/<script[\s\S]*?<\/script>/gi, "");
  text = text.replace(/<style[\s\S]*?<\/style>/gi, "");

  text = text.replace(/<\/(p|div|section|article|li|tr|h1|h2|h3|h4|h5|h6)>/gi, "\n");
  text = text.replace(/<br\s*\/?>/gi, "\n");

  text = text.replace(/<\/?[^>]+>/g, "");

  text = text
    .replace(/&nbsp;/gi, " ")
    .replace(/&amp;/gi, "&")
    .replace(/&quot;/gi, "\"")
    .replace(/&#39;/gi, "'")
    .replace(/&lt;/gi, "<")
    .replace(/&gt;/gi, ">");

  text = text
    .split("\n")
    .map(line => line.trim())
    .filter(Boolean)
    .join("\n");

  return text;
}

function getDomain(url) {
  try {
    const u = new URL(url);
    return u.hostname.replace(/^www\./, "");
  } catch (e) {
    return "";
  }
}

function extractTag(html, tag) {
  if (!html) return "";
  const regex = new RegExp(`<${tag}[^>]*>([\\s\\S]*?)<\\/${tag}>`, "i");
  const match = html.match(regex);
  return match ? String(match[1]).trim() : "";
}

function cleanEntityName(text) {
  if (!text) return "";
  return String(text)
    .replace(/reviews?/gi, "")
    .replace(/read customer service/gi, "")
    .replace(/customer service/gi, "")
    .replace(/\|.*$/g, "")
    .replace(/-.*$/g, "")
    .replace(/\s+/g, " ")
    .trim();
}

// NEW: extract slug from Trustpilot-like URL
function extractEntitySlugFromUrl(url) {
  if (!url) return "";
  try {
    const u = new URL(url);
    // Example path: /review/maclear.ch
    const parts = u.pathname.split("/").filter(Boolean); // ["review","maclear.ch"]
    const reviewIdx = parts.findIndex(p => p.toLowerCase() === "review");
    if (reviewIdx >= 0 && parts[reviewIdx + 1]) {
      return parts[reviewIdx + 1].trim();
    }
    // Generic fallback: last path segment
    return (parts[parts.length - 1] || "").trim();
  } catch (e) {
    // If it's not a valid URL string
    const m = String(url).match(/\/review\/([^/?#]+)/i);
    return m ? m[1].trim() : "";
  }
}

const newItems = [];

for (const item of items) {
  const json = item.json || {};
  let result = json;

  // If wrapped in { results: [ ... ] }, extract first result
  if (Array.isArray(json.results) && json.results.length > 0) {
    result = json.results[0];
  }

  // URL can come from different places depending on your node
  const url = result.url || json.url || "";
  const html = result.content || result.html || "";

  const pageText = htmlToText(html);
  const domain = getDomain(url);

  // Trustpilot-like slug: maclear.ch / apify.com
  const entitySlug = extractEntitySlugFromUrl(url) || null;

  // Prefer h1/title, but fallback to slug if extraction is messy
  const h1Text = extractTag(html, "h1");
  const titleText = extractTag(html, "title");
  const extractedName = cleanEntityName(h1Text || titleText);

  const entityName = extractedName
    ? extractedName
    : (entitySlug ? entitySlug : null);

  newItems.push({
    json: {
      url,
      domain,
      entity_slug: entitySlug,
      entity_name: entityName,
      page_text: pageText,
      raw_html_length: html.length
    }
  });
}

return newItems;

// This Function node takes the LLM "output" field (JSON string),
// parses it, and flattens it into a structure ready to be saved
// into Google Sheets (one row per item).

const newItems = [];

for (const item of items) {
  const rawOutput = item.json.output;

  let parsed;
  if (typeof rawOutput === 'string') {
    try {
      parsed = JSON.parse(rawOutput);
    } catch (error) {
      // If parsing fails, keep debug info
      newItems.push({
        json: {
          parse_error: true,
          error_message: error.message || 'Failed to parse LLM output',
          original_output: rawOutput
        }
      });
      continue;
    }
  } else if (typeof rawOutput === 'object' && rawOutput !== null) {
    // In case the LLM node already returns an object
    parsed = rawOutput;
  } else {
    newItems.push({
      json: {
        parse_error: true,
        error_message: 'Unexpected output type from LLM',
        original_output: rawOutput
      }
    });
    continue;
  }

  const ratingDist = parsed.rating_distribution || {};
  const sentimentDist = parsed.sentiment_distribution || {};
  const topicDist = parsed.topic_distribution || {};

  // Build a flat JSON object for Google Sheets
  const flat = {
    // Basic metadata
    source_url: parsed.source_url || "",
    source_domain: parsed.source_domain || "",
    estimated_total_reviews: parsed.estimated_total_reviews ?? null,
    average_rating: parsed.average_rating ?? null,
    rating_scale_max: parsed.rating_scale_max ?? 5,

    // Rating distribution (1‚Äì5)
    rating_1: ratingDist["1"] ?? 0,
    rating_2: ratingDist["2"] ?? 0,
    rating_3: ratingDist["3"] ?? 0,
    rating_4: ratingDist["4"] ?? 0,
    rating_5: ratingDist["5"] ?? 0,

    // Sentiment distribution
    sentiment_positive: sentimentDist.positive ?? 0,
    sentiment_neutral: sentimentDist.neutral ?? 0,
    sentiment_negative: sentimentDist.negative ?? 0,

    // Topic distribution
    topic_pricing: topicDist.pricing ?? 0,
    topic_usability: topicDist.usability ?? 0,
    topic_features: topicDist.features ?? 0,
    topic_support: topicDist.support ?? 0,
    topic_performance: topicDist.performance ?? 0,
    topic_other: topicDist.other ?? 0,

    // Arrays ‚Üí strings
    top_positive_keywords: Array.isArray(parsed.top_positive_keywords)
      ? parsed.top_positive_keywords.join(", ")
      : "",

    top_negative_keywords: Array.isArray(parsed.top_negative_keywords)
      ? parsed.top_negative_keywords.join(", ")
      : "",

    top_praises: Array.isArray(parsed.top_praises)
      ? parsed.top_praises.join("\n")
      : "",

    top_complaints: Array.isArray(parsed.top_complaints)
      ? parsed.top_complaints.join("\n")
      : "",

    // Text fields
    recent_trend_summary: parsed.recent_trend_summary || "",
    data_quality_notes: parsed.data_quality_notes || ""
  };

  newItems.push({ json: flat });
}

return newItems;

function truncateTo255Smart(value) {
  if (!value) return "";

  const text = String(value);

  // If already within limit, return as is
  if (text.length <= 255) {
    return text;
  }

  // Cut to max length
  const truncated = text.slice(0, 255);

  // Try to avoid cutting words
  const lastSpace = truncated.lastIndexOf(" ");

  return lastSpace > 200
    ? truncated.slice(0, lastSpace)
    : truncated;
}

// Apply truncation to the Salesforce field
