// Extract LinkedIn company URL from Google search results
const lead = $('Normalize Lead Data1').first().json;
const searchResults = $input.all().map(item => item.json);

let linkedInUrl = null;
let searchSuccess = false;
let matchedBy = null;

// Get company name and domain for matching
const companyName = (lead.company || '').toLowerCase().trim();
const emailDomain = (lead.emailDomain || '').toLowerCase().split('.')[0]; // e.g., "elima" from "elima.io"

// Build search terms for matching (company name, domain, variations)
const searchTerms = new Set();
if (companyName) {
  searchTerms.add(companyName.replace(/[^a-z0-9]/g, ''));
  // Remove common suffixes
  searchTerms.add(companyName.replace(/\s*(inc|llc|ltd|corp|company|co|technologies|tech|solutions)\.?$/i, '').replace(/[^a-z0-9]/g, ''));
}
if (emailDomain) {
  searchTerms.add(emailDomain.replace(/[^a-z0-9]/g, ''));
}

// Remove empty strings
searchTerms.delete('');

try {
  // Flatten results array
  const results = searchResults.flat();

  // First pass: Find LinkedIn company URL that matches our company name/domain
  for (const result of results) {
    const url = (result.url || result.link || '');
    const title = (result.title || '');

    // Match LinkedIn company pages (handles regional domains like in.linkedin.com, uk.linkedin.com, etc.)
    const linkedInCompanyMatch = url.match(/linkedin\.com\/company\/([^\/\?#]+)/i);

    if (linkedInCompanyMatch) {
      // Skip LinkedIn help pages
      if (url.toLowerCase().includes('/help/')) continue;

      const companySlug = linkedInCompanyMatch[1].toLowerCase().replace(/[^a-z0-9]/g, '');
      const titleNormalized = title.toLowerCase().replace(/[^a-z0-9]/g, '');

      // Check if slug or title matches any of our search terms
      for (const term of searchTerms) {
        if (term.length < 2) continue; // Skip very short terms

        const isSlugMatch = companySlug.includes(term) || term.includes(companySlug);
        const isTitleMatch = titleNormalized.includes(term);

        if (isSlugMatch || isTitleMatch) {
          linkedInUrl = url;
          searchSuccess = true;
          matchedBy = isSlugMatch ? `slug: ${companySlug}` : `title: ${title}`;
          break;
        }
      }

      if (searchSuccess) break;
    }
  }

  // Fallback: If no match found, use first valid LinkedIn company URL (not help page)
  if (!searchSuccess) {
    for (const result of results) {
      const url = (result.url || result.link || '');

      if (url.match(/linkedin\.com\/company\/[^\/\?#]+/i) && !url.toLowerCase().includes('/help/')) {
        linkedInUrl = url;
        searchSuccess = true;
        matchedBy = 'fallback-first-company-url';
        break;
      }
    }
  }

} catch (error) {
  console.log('Error parsing search results:', error.message);
}

return {
  ...lead,
  linkedInUrl: linkedInUrl,
  linkedInSearchSuccess: searchSuccess,
  matchedBy: matchedBy,
  searchTermsUsed: Array.from(searchTerms),
  googleSearchRaw: JSON.stringify(searchResults).substring(0, 500)
};

// Merge LinkedIn company data with lead
const lead = $('Extract LinkedIn URL').first().json;
const linkedInData = $input.all().map(item => item.json);

let companyData = {};

try {
  // Handle array or object response
  const results = Array.isArray(linkedInData) ? linkedInData.flat() : [linkedInData];
  const company = results[0];

  if (company) {
    // Build headquarters string from mainAddress if available
    let headquarters = '';
    if (company.mainAddress) {
      const addr = company.mainAddress;
      headquarters = [
        addr.addressLocality,
        addr.addressRegion,
        addr.addressCountry
      ].filter(Boolean).join(', ');
    }

    // Parse specialties - can be string or array
    let specialties = company.Specialties || company.specialties || '';
    if (typeof specialties === 'string') {
      specialties = specialties.split(',').map(s => s.trim()).filter(Boolean);
    }

    companyData = {
      // Basic info
      name: company.name || lead.company,
      slogan: company.slogan || '',
      description: company.description || '',
      website: company.website || '',

      // Industry & Type
      industry: company.Industry || company.industry || '',
      companyType: company.Type || company.type || '',

      // Size & Employees
      employeeCount: company.numberOfEmployees || '',
      employeeRange: company['Company size'] || company.companySize || '',

      // Location
      headquarters: company.Headquarters || headquarters || '',
      fullAddress: company.mainAddress ? [
        company.mainAddress.streetAddress,
        company.mainAddress.addressLocality,
        company.mainAddress.addressRegion,
        company.mainAddress.postalCode,
        company.mainAddress.addressCountry
      ].filter(Boolean).join(', ') : '',
      allAddresses: company.addresses || [],

      // Dates & Stats
      foundedYear: company.Founded || company.founded || '',
      followerCount: company.FollowersCount || company.followersCount || '',

      // Other
      specialties: specialties,
      linkedInUrl: company.url || lead.linkedInUrl,
      logoUrl: company.logo || '',

      // Related companies (useful for competitive analysis)
      similarCompanies: (company.similarPages || []).slice(0, 5).map(p => ({
        name: p.name,
        industry: p.industry,
        linkedInUrl: p.linkeinUrl || p.linkedInUrl
      }))
    };
  }
} catch (error) {
  console.log('Error parsing LinkedIn data:', error.message);
}

return {
  ...lead,
  enrichedCompany: {
    name: companyData.name || lead.company || 'Unknown',
    slogan: companyData.slogan || '',
    industry: companyData.industry || '',
    description: companyData.description || '',
    employees: companyData.employeeCount || '',
    employeeRange: companyData.employeeRange || '',
    headquarters: companyData.headquarters || '',
    fullAddress: companyData.fullAddress || '',
    website: companyData.website || '',
    foundedYear: companyData.foundedYear || '',
    specialties: Array.isArray(companyData.specialties) ? companyData.specialties.join(', ') : companyData.specialties,
    linkedInUrl: companyData.linkedInUrl || '',
    logoUrl: companyData.logoUrl || '',
    followerCount: companyData.followerCount || '',
    companyType: companyData.companyType || '',
    similarCompanies: companyData.similarCompanies || []
  },
  enrichmentSource: 'linkedin',
  enrichmentSuccessful: !!(companyData.name && (companyData.industry || companyData.description)),
  enrichmentTimestamp: new Date().toISOString()
};

// Normalize and prepare lead data from form submission
const input = $input.first().json;

// Extract and normalize fields from form
const lead = {
  leadId: `LEAD-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString(),

  // Contact Information (map from form field labels)
  email: (input['Email'] || input.email || '').toLowerCase().trim(),
  firstName: (input['First Name'] || input.firstName || '').trim(),
  lastName: (input['Last Name'] || input.lastName || '').trim(),
  phone: (input['Phone'] || input.phone || '').replace(/[^0-9+]/g, ''),

  // Company Information
  company: (input['Company'] || input.company || '').trim(),
  jobTitle: (input['Job Title'] || input.jobTitle || '').trim(),

  // Lead Context
  message: (input['What can we help you with?'] || input.message || '').trim(),
  budget: input['Budget Range'] || input.budget || '',
  timeline: input['Timeline'] || input.timeline || '',
  source: input['How did you hear about us?'] || input.source || 'direct',

  // Derived fields
  fullName: '',
  emailDomain: '',

  // Validation
  isValid: false,
  validationErrors: []
};

// Build full name
lead.fullName = `${lead.firstName} ${lead.lastName}`.trim() || 'Unknown';

// Extract domain from email for enrichment
if (lead.email && lead.email.includes('@')) {
  lead.emailDomain = lead.email.split('@')[1];

  // Skip common personal email domains for enrichment
  const personalDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com', 'icloud.com', 'aol.com', 'mail.com', 'protonmail.com'];
  lead.isBusinessEmail = !personalDomains.includes(lead.emailDomain.toLowerCase());
} else {
  lead.isBusinessEmail = false;
}

// Basic validation
const validationErrors = [];
if (!lead.email || !lead.email.includes('@')) {
  validationErrors.push('Invalid or missing email');
}
if (!lead.firstName) {
  validationErrors.push('Missing first name');
}

lead.isValid = validationErrors.length === 0;
lead.validationErrors = validationErrors;

// Store raw form data
lead.rawFormData = JSON.stringify(input);

return lead;

