// Get lead data from whichever path executed (enriched or fallback)
let lead;
try {
  lead = $('Merge LinkedIn Data').first().json;
} catch (e) {
  lead = $('Skip Enrichment (Fallback)').first().json;
}

const aiResponse = $input.first().json;

// Parse AI response (handle both string and object responses)
let qualification;
try {
  qualification = typeof aiResponse.message?.content === 'string' 
    ? JSON.parse(aiResponse.message.content) 
    : aiResponse.message?.content || aiResponse;
} catch (e) {
  // Fallback if AI response isn't proper JSON
  qualification = {
    qualificationScore: 50,
    qualificationTier: 'Cold',
    buyerPersona: 'Unknown',
    urgencyLevel: 'Low',
    budgetFit: 'Unknown',
    keyInsights: ['AI qualification parsing failed - manual review required'],
    suggestedNextSteps: ['Manual qualification required'],
    personalizedTalkingPoints: [],
    riskFactors: ['AI qualification error'],
    idealOutreachChannel: 'Email',
    suggestedOutreachTiming: '48h',
    qualificationReasoning: 'Automated qualification parsing failed - review raw response'
  };
}

// Build final qualified lead object
const qualifiedLead = {
  // Core lead data
  leadId: lead.leadId,
  timestamp: lead.timestamp,

  // Contact info
  email: lead.email,
  firstName: lead.firstName,
  lastName: lead.lastName,
  fullName: lead.fullName,
  phone: lead.phone,
  jobTitle: lead.jobTitle,

  // Company info
  company: lead.company,
  enrichedCompany: lead.enrichedCompany,

  // Lead context
  message: lead.message,
  budget: lead.budget,
  timeline: lead.timeline,
  source: lead.source,

  // Data quality flags
  isBusinessEmail: lead.isBusinessEmail,
  enrichmentSuccessful: lead.enrichmentSuccessful,
  enrichmentSource: lead.enrichmentSource,

  // AI Qualification Results
  qualification: {
    score: qualification.qualificationScore || 0,
    tier: qualification.qualificationTier || 'cold',
    buyerPersona: qualification.buyerPersona || 'Unknown',
    urgencyLevel: qualification.urgencyLevel || 'Low',
    budgetFit: qualification.budgetFit || 'Unknown',
    reasoning: qualification.qualificationReasoning || ''
  },

  // AI Insights
  insights: {
    keyInsights: qualification.keyInsights || [],
    suggestedNextSteps: qualification.suggestedNextSteps || [],
    talkingPoints: qualification.personalizedTalkingPoints || [],
    riskFactors: qualification.riskFactors || []
  },

  // Outreach Recommendations
  outreach: {
    channel: qualification.idealOutreachChannel || 'Email',
    timing: qualification.suggestedOutreachTiming || '48h',
    priority: qualification.qualificationScore >= 80 ? 'High' : 
              qualification.qualificationScore >= 60 ? 'Medium' : 'Low'
  },

  // Processing metadata
  processedAt: new Date().toISOString(),
  pipelineVersion: '2.1'
};

return qualifiedLead;

