function calculateBearing(lat1, lon1, lat2, lon2) {
  const œÜ1 = lat1 * Math.PI / 180;
  const œÜ2 = lat2 * Math.PI / 180;
  const ŒîŒª = (lon2 - lon1) * Math.PI / 180;
