function estimateElevation(distanceKm) {
  const ISS_ALTITUDE = 408;
  const elevation = Math.atan2(ISS_ALTITUDE, distanceKm) * 180 / Math.PI;
  return Math.max(0, Math.min(90, elevation));
}

