// Process each item from the merge
const results = [];

for (const item of $input.all()) {
  // Get the original CRM data (passed through from earlier nodes)
  const crmData = item.json;

  // Extract Explorium data (it's nested in enriched_data array)
  const exploriumArray = item.json.enriched_data || [];

  if (exploriumArray.length === 0) {
    // No Explorium data returned, skip this champion
    continue;
  }

  const exploriumData = exploriumArray[0].data;

  // Get current company from both sources
  const crmCompany = (crmData.company || '').toLowerCase().trim();
  const exploriumCompany = (exploriumData.company_name || '').toLowerCase().trim();

  // Detect if job changed
  const jobChanged = crmCompany !== exploriumCompany && exploriumCompany !== '';

  // Find the primary/current job from experience array
  const currentJob = exploriumData.experience?.find(exp => exp.is_primary === true) || exploriumData.experience?.[0];

  results.push({
    // Original CRM data
    champion_id: crmData.champion_id,
    name: crmData.name,
    email: crmData.email,
    old_company: crmData.company,
    old_title: crmData.title,
    relationship_strength: crmData.relationship_strength,
    last_contact_date: crmData.last_contact_date,
    deals_influenced: crmData.deals_influenced,
    relationship_notes: crmData.relationship_notes,
    linkedin_url: crmData.linkedin_url,

    // Explorium current employment data
    new_company: exploriumData.company_name,
    new_title: exploriumData.job_title,
    new_company_website: exploriumData.company_website,
    new_company_linkedin: exploriumData.company_linkedin,
    job_department: exploriumData.job_department,
    job_seniority: exploriumData.job_seniority_level,

    // Additional Explorium data
    current_professional_email: exploriumData.professions_email,
    mobile_phone: exploriumData.mobile_phone,
    location: `${exploriumData.city}, ${exploriumData.region_name}`,

    // Job history context (useful for email personalization)
    experience_summary: currentJob?.summary || '',
    start_date: currentJob?.start_date || '',

    // Store prospect_id for future use
    explorium_prospect_id: exploriumArray[0].prospect_id,

    // Flag if changed
    job_changed: jobChanged,

    // Store full Explorium data for logging (as string)
    explorium_full_data: JSON.stringify(exploriumData)
  });
}

return results;

const allItems = [];

// Get ALL champions from the original sheet read
const allChampions = $('Get row(s) in sheet').all();

// Get the prospect IDs that were just created (FALSE branch)
const newProspectIds = new Map();
try {
  const matchResults = $('Explorium API').all();
  for (const item of matchResults) {
    const data = item.json;
    if (data.matched_prospects && data.matched_prospects.length > 0) {
      const match = data.matched_prospects[0];
      const prospectId = match.prospect_id;
      const inputEmail = match.input?.email;
      if (inputEmail && prospectId) {
        newProspectIds.set(inputEmail, prospectId);
      }
    }
  }
} catch (e) {
  console.log('Error getting new prospect IDs:', e);
}

// Process all champions and add prospect IDs where needed
for (const champion of allChampions) {
  const champData = champion.json;

  // If they didn't have a prospect_id, check if we just got one
  if (!champData.explorium_prospect_id && newProspectIds.has(champData.email)) {
    champData.explorium_prospect_id = newProspectIds.get(champData.email);
  }

  allItems.push(champData);
}

return allItems.map(item => ({ json: item }));

// Get champions explicitly from the IF node
const champions = $('Did Job Change?').all();

// Get CRM companies explicitly from Google Sheets
const crmCompanies = $('Pull Company Records to Check if New Company in CRM').all();

console.log('Champions:', champions.length);
console.log('CRM Companies:', crmCompanies.length);

const results = [];

for (const champ of champions) {
  const champData = champ.json;
  const newCompanyLower = (champData.new_company || '').toLowerCase().trim();

  console.log('Matching champion company:', champData.new_company);

  // Find match by company name only
  let match = null;

  for (const crmComp of crmCompanies) {
    const crmCompData = crmComp.json;
    const crmCompanyLower = (crmCompData.companyName || '').toLowerCase().trim();

    console.log('  vs CRM:', crmCompData.companyName);

    // Match if names contain each other
    if (newCompanyLower.includes(crmCompanyLower) || crmCompanyLower.includes(newCompanyLower)) {
      match = crmCompData;
      console.log('  ‚úì MATCH!');
      break;
    }
  }

  results.push({
    ...champData,
    matched_to_crm: !!match,
    relationship_type: match?.relationship_type || 'New',
    crm_company_id: match?.company_ID || null,
    open_opportunity: match?.open_opportunity || false,
    opportunity_stage: match?.opportunity_stage || null,
    account_owner: match?.accountOwner || null,
    account_owner_email: match?.account_owner_email || null
  });
}

console.log('Final results:', results.length);

return results;

