const results = [];

for (const item of $input.all()) {
  const data = item.json;

  // Extract company data from enrichment response
  const companyData = data.enrichmentsResponse?.[0]?.response?.data?.[0]?.data || {};

  // Parse employee count (it's a range like "201-500")
  const employeeRange = companyData.number_of_employees_range || '';
  let employeeCount = 0;
  if (employeeRange.includes('-')) {
    const parts = employeeRange.split('-');
    employeeCount = parseInt(parts[1]); // Use upper bound
  }

  // Get key company data
  const revenueRange = companyData.yearly_revenue_range || '';
  const fundingTotal = companyData.known_funding_total_value || 0;
  const industry = companyData.linkedin_industry_category || '';
  const techStack = companyData.full_tech_stack || [];
  const acquiredBy = companyData.acquired_by || null;

  // === CRM STATUS EXTRACTION ===
  const accountStatus = data.relationship_type || 'New';
  const dealStatus = data.open_opportunity ? 'Open' : 'None';
  const accountOwner = data.account_owner || 'Unassigned';
  const contractValue = data.contract_value || null;

  console.log('Processing:', data.name || data.new_company, 'Account Status:', accountStatus);

  // === ICP SCORING (0-100) ===
  let icpScore = 0;

  // 1. Company Size (0-30 points)
  if (employeeCount >= 200 && employeeCount <= 500) {
    icpScore += 30;
  } else if (employeeCount >= 100 && employeeCount < 200) {
    icpScore += 25;
  } else if (employeeCount > 500 && employeeCount <= 1000) {
    icpScore += 20;
  } else if (employeeCount > 1000) {
    icpScore += 10;
  } else {
    icpScore += 5;
  }

  // 2. Funding (0-25 points)
  if (fundingTotal > 30000000) {
    icpScore += 25;
  } else if (fundingTotal > 15000000) {
    icpScore += 20;
  } else if (fundingTotal > 5000000) {
    icpScore += 15;
  } else {
    icpScore += 5;
  }

  // 3. Revenue (0-20 points)
  if (revenueRange.includes('M')) {
    if (revenueRange.includes('10M') || revenueRange.includes('25M') || revenueRange.includes('50M')) {
      icpScore += 20;
    } else {
      icpScore += 10;
    }
  }

  // 4. Tech Stack Match (0-25 points)
  const relevantTech = ['Salesforce', 'ChurnZero', 'Zendesk', 'Marketo', 'Slack', 'HubSpot', 'Gainsight'];
  const techMatches = techStack.filter(tech => 
    relevantTech.some(rt => tech.toLowerCase().includes(rt.toLowerCase()))
  ).length;

  if (techMatches >= 3) {
    icpScore += 25;
  } else if (techMatches >= 1) {
    icpScore += 15;
  } else {
    icpScore += 5;
  }

  icpScore = Math.min(icpScore, 100);

  // === RELATIONSHIP SCORING (0-100) ===
  const relationshipStrength = data.relationship_strength || 'Cold';
  const dealsInfluenced = data.deals_influenced || 0;

  let relationshipScore = 0;

  // Base relationship strength (0-40 points)
  if (relationshipStrength === 'Hot') {
    relationshipScore += 40;
  } else if (relationshipStrength === 'Warm') {
    relationshipScore += 25;
  } else {
    relationshipScore += 10;
  }

  // Deals influenced (0-20 points)
  relationshipScore += Math.min(dealsInfluenced * 5, 20);

  // CRM Status Boost (0-40 points)
  let crmBoost = 0;

  // Check account status directly
  if (accountStatus === 'Customer') {
    crmBoost += 40; // Existing customer - highest priority
    console.log('‚úì Customer detected - adding 40 boost');
  } else if (accountStatus === 'Prospect' && dealStatus === 'Open') {
    crmBoost += 25; // Already in pipeline
    console.log('‚úì Prospect with open deal - adding 25 boost');
  } else if (accountStatus === 'Prospect') {
    crmBoost += 15; // Known prospect
    console.log('‚úì Prospect - adding 15 boost');
  } else {
    crmBoost += 10; // New prospect
    console.log('‚úì New prospect - adding 10 boost');
  }

  console.log('Relationship Score Before Boost:', relationshipScore);
  console.log('CRM Boost:', crmBoost);

  relationshipScore = Math.min(relationshipScore + crmBoost, 100);

  console.log('Final Relationship Score:', relationshipScore);

  // === TIMING SCORING (0-100) ===
  const startDate = data.start_date || '';
  let daysAtCompany = 0;

  if (startDate) {
    const start = new Date(startDate);
    const now = new Date();
    daysAtCompany = Math.floor((now - start) / (1000 * 60 * 60 * 24));
  }

  let timingScore = 0;

  if (daysAtCompany >= 90 && daysAtCompany <= 365) {
    timingScore += 50;
  } else if (daysAtCompany >= 30 && daysAtCompany < 90) {
    timingScore += 30;
  } else if (daysAtCompany > 365 && daysAtCompany <= 730) {
    timingScore += 40;
  } else {
    timingScore += 15;
  }

  if (fundingTotal > 20000000) {
    timingScore += 30;
  } else if (fundingTotal > 0) {
    timingScore += 20;
  }

  if (acquiredBy) {
    timingScore += 20;
  }

  timingScore = Math.min(timingScore, 100);

  // === COMBINED OPPORTUNITY SCORE ===
  const opportunityScore = Math.round(
    (icpScore * 0.4) +
    (relationshipScore * 0.4) +
    (timingScore * 0.2)
  );

  let priority;
  if (opportunityScore >= 75) {
    priority = 'HOT';
  } else if (opportunityScore >= 50) {
    priority = 'WARM';
  } else {
    priority = 'COLD';
  }

  // Outreach Strategy
  let outreachStrategy;
  if (accountStatus === 'Customer') {
    outreachStrategy = 'Congratulations + New Champion at Account';
  } else if (dealStatus === 'Open') {
    outreachStrategy = 'Warm Handoff + Deal Acceleration';
  } else if (accountStatus === 'Prospect') {
    outreachStrategy = 'Re-engagement + Champion Credibility';
  } else {
    outreachStrategy = 'Cold Outreach + Champion Credibility';
  }

  console.log('Final Scores - ICP:', icpScore, 'Relationship:', relationshipScore, 'Timing:', timingScore, 'Overall:', opportunityScore);

  // Push to results array
  results.push({
    json: {
      ...data,
      company_employee_count: employeeRange,
      company_revenue: revenueRange,
      company_funding_total: fundingTotal,
      company_industry: industry,
      company_tech_stack: techStack.slice(0, 10),
      company_acquired_by: acquiredBy,
      icp_score: icpScore,
      relationship_score: relationshipScore,
      timing_score: timingScore,
      opportunity_score: opportunityScore,
      priority: priority,
      outreach_strategy: outreachStrategy,
      scoring_breakdown: {
        icp: icpScore,
        relationship: relationshipScore,
        timing: timingScore,
        days_at_company: daysAtCompany,
        crm_boost: crmBoost
      }
    }
  });
}

console.log('Total items processed:', results.length);

return results;

