// Spin text template - customize with your name and style
const spinTextTemplate = `{Thanks for|Really appreciate|Awesome to see|Loved seeing|Grateful for} {your|you taking a moment to|you recently} {comment on|engaging with|reaction to|thoughts on|feedback on} {my|our|the} {post|recent post|latest update|content|recent article}.

{Would love to|Happy to|Let's definitely|Open to|Excited to} connect {and|so we can|to} {exchange ideas|share insights|continue the conversation|explore synergies|stay in touch}. {Always enjoy connecting with|Always excited to meet|Love meeting|Great connecting with} {like-minded professionals|people in the space|others exploring similar topics|fellow thought leaders}.

{Cheers|Best regards|Thanks again|Looking forward|Talk soon},
Amaan sarfaraz`;

// Parse spin text to generate unique variations
function parseSpinText(text) {
  return text.replace(/\{([^}]+)\}/g, (match, options) => {
    const choices = options.split('|');
    return choices[Math.floor(Math.random() * choices.length)];
  });
}

const generatedMessage = parseSpinText(spinTextTemplate);
const commenterData = $('Loop Over Items').item.json;
const commenterName = commenterData.author?.firstName || 'there';
const finalMessage = `Hi ${commenterName},\n\n${generatedMessage}`;

// Keep under LinkedIn's 300 character limit
const trimmedMessage = finalMessage.length > 300 
  ? finalMessage.substring(0, 297) + '...'
  : finalMessage;

return {
  generatedMessage: trimmedMessage,
  commenterName: commenterName,
  commenterProfileId: commenterData.author?.publicIdentifier,
  commentText: commenterData.commentText
};

const commenterData = $('Loop Over Items').item.json;
const message = $('Generate Message').first().json;

return {
  success: true,
  commenterName: message.commenterName,
  commenterProfileId: message.commenterProfileId,
  messageSent: message.generatedMessage,
  timestamp: new Date().toISOString(),
  commentText: message.commentText
};

const relationshipData = $('Check Relationship').first().json;
const commenterData = $('Loop Over Items').item.json;

return {
  skipped: true,
  reason: relationshipData.isConnected ? 'Already connected' : 'Pending invitation',
  commenterName: commenterData.author?.firstName || 'Unknown',
  commenterProfileId: commenterData.author?.publicIdentifier,
  isConnected: relationshipData.isConnected,
  invitationSent: relationshipData.invitationSent,
  timestamp: new Date().toISOString()
};

const allItems = $input.all();
const successfulRequests = allItems.filter(item => item.json.success === true);
const skippedRequests = allItems.filter(item => item.json.skipped === true);

const summary = {
  totalCommenters: allItems.length,
  connectionRequestsSent: successfulRequests.length,
  skippedConnections: skippedRequests.length,
  skippedReasons: {
    alreadyConnected: skippedRequests.filter(item => item.json.reason === 'Already connected').length,
    pendingInvitation: skippedRequests.filter(item => item.json.reason === 'Pending invitation').length
  },
  processedAt: new Date().toISOString(),
  successRate: allItems.length > 0 
    ? ((successfulRequests.length / allItems.length) * 100).toFixed(2) + '%'
    : '0%'
};

return {
  summary,
  successfulRequests: successfulRequests.map(item => ({
    name: item.json.commenterName,
    profileId: item.json.commenterProfileId,
    messageSent: item.json.messageSent
  })),
  skippedRequests: skippedRequests.map(item => ({
    name: item.json.commenterName,
    profileId: item.json.commenterProfileId,
    reason: item.json.reason
  }))
};
