// Get OpenAI API key from Workflow Configuration node
const apiKey = $('Workflow Configuration1').item.json.openaiApiKey;
const imageUrl = $input.item.json.displayUrl || $input.item.json.coverUrl;
const accountName = $input.item.json.ownerUsername || $input.item.json.authorMeta?.name || 'unknown';

// Detect platform based on data structure
const platform = $input.item.json.videoMeta ? 'TikTok' : 'Instagram';

// Call OpenAI GPT-4o Vision API
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://api.openai.com/v1/chat/completions',
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${apiKey}`
  },
  body: {
    model: 'gpt-4o',
    messages: [
      {
        role: 'user',
        content: [
          {
            type: 'text',
            text: 'Analyze this image and extract visual attributes in JSON format: color_palette (array of 3-5 dominant hex colors), composition_style (string), mood_emotion (string), text_design_elements (object with font_style, text_placement, text_content). Return only valid JSON.'
          },
          {
            type: 'image_url',
            image_url: {
              url: imageUrl
            }
          }
        ]
      }
    ],
    max_tokens: 500
  },
  json: true
});

// Parse the AI response
let analysis;
try {
  const content = response.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  } else {
    analysis = JSON.parse(content);
  }
} catch (error) {
  analysis = {
    color_palette: [],
    composition_style: 'Unable to analyze',
    mood_emotion: 'Unable to analyze',
    text_design_elements: {}
  };
}

// Return analyzed image with metadata
return {
  json: {
    imageUrl: imageUrl,
    accountName: accountName,
    platform: platform,
    colorPalette: analysis.color_palette || [],
    compositionStyle: analysis.composition_style || '',
    moodEmotion: analysis.mood_emotion || '',
    textDesignElements: analysis.text_design_elements || {},
    rawAnalysis: response.choices[0].message.content
  }
};

// Generate Comprehensive Comparison Report using OpenAI

const items = $input.all();
const aggregatedData = items[0].json;

// Get OpenAI API key from Workflow Configuration
const openaiApiKey = $('Workflow Configuration1').item.json.openaiApiKey;

if (!openaiApiKey) {
  throw new Error('OpenAI API key not found in Workflow Configuration');
}

// Prepare data summary for OpenAI prompt
const dataSummary = JSON.stringify(aggregatedData, null, 2);

// Create comprehensive prompt for OpenAI
const prompt = `You are a social media visual content strategy expert. Analyze the following data from multiple social media platforms (Instagram, Pinterest, TikTok) and generate a comprehensive comparison report.

Data:
${dataSummary}

Please provide a detailed report with the following sections:

1. COMPETITIVE COMPARISON MATRIX
   - Create a matrix showing visual patterns by account
   - Compare color schemes, composition styles, content types
   - Highlight top-performing visual elements per account

2. PLATFORM-SPECIFIC WINNING PATTERNS
   - Instagram: What visual patterns drive the most engagement?
   - Pinterest: What visual styles get the most saves/repins?
   - TikTok: What visual elements capture attention?

3. CROSS-PLATFORM INSIGHTS AND TRENDS
   - Common visual themes across all platforms
   - Platform-specific vs universal visual strategies
   - Emerging visual trends identified

4. RECOMMENDED ACTIONS
   - Specific actionable recommendations to improve visual content strategy
   - Quick wins that can be implemented immediately
   - Long-term strategic visual content improvements

Format the report in a clear, structured manner with headers and bullet points.`;

// Call OpenAI API
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://api.openai.com/v1/chat/completions',
  headers: {
    'Authorization': `Bearer ${openaiApiKey}`,
    'Content-Type': 'application/json'
  },
  body: {
    model: 'gpt-4',
    messages: [
      {
        role: 'system',
        content: 'You are a social media visual content strategy expert specializing in competitive analysis and actionable insights.'
      },
      {
        role: 'user',
        content: prompt
      }
    ],
    temperature: 0.7,
    max_tokens: 2500
  },
  json: true
});

// Extract the generated report
const report = response.choices[0].message.content;

// Parse report into sections
const sections = {
  fullReport: report,
  competitiveMatrix: extractSection(report, 'COMPETITIVE COMPARISON MATRIX', 'PLATFORM-SPECIFIC WINNING PATTERNS'),
  platformPatterns: extractSection(report, 'PLATFORM-SPECIFIC WINNING PATTERNS', 'CROSS-PLATFORM INSIGHTS'),
  crossPlatformInsights: extractSection(report, 'CROSS-PLATFORM INSIGHTS', 'RECOMMENDED ACTIONS'),
  recommendedActions: extractSection(report, 'RECOMMENDED ACTIONS', null),
  generatedAt: new Date().toISOString(),
  dataAnalyzed: {
    totalAccounts: aggregatedData.totalAccounts || 0,
    totalImages: aggregatedData.totalImages || 0,
    platforms: aggregatedData.platforms || []
  }
};

// Helper function to extract sections
function extractSection(text, startMarker, endMarker) {
  const startIndex = text.indexOf(startMarker);
  if (startIndex === -1) return '';

  const contentStart = startIndex + startMarker.length;

  if (endMarker) {
    const endIndex = text.indexOf(endMarker, contentStart);
    if (endIndex === -1) return text.substring(contentStart).trim();
    return text.substring(contentStart, endIndex).trim();
  }

  return text.substring(contentStart).trim();
}

// Return formatted report with all sections
return [{
  json: sections
}];
